(55,9,1,3,10,9,'Explore and implement advanced technologies in propulsion, guidance, targeting, and materials to enhance system performance.','A','','admin','2024-11-25 12:31:42',NULL,NULL,'Nil'),
(56,9,1,5,0,0,'Design and implement training programs to build technical expertise and operational readiness for the project team.','A','','admin','2024-11-25 12:33:08',NULL,NULL,'Nil'),
