  `LetterTypeId` bigint DEFAULT NULL,
  `DeliveryTypeId` bigint DEFAULT NULL,
  `DestinationId` bigint DEFAULT NULL,
  `RefNo` varchar(150) DEFAULT NULL,
  `RefDate` date DEFAULT NULL,
