  `CreatedDate` datetime DEFAULT NULL,
  `ModifiedBy` varchar(100) DEFAULT NULL,
  `ModifiedDate` datetime DEFAULT NULL,
  `IsActive` int DEFAULT '1',
  PRIMARY KEY (`DakCreateId`)
