/* Procedure structure for procedure `Action_Monitoring` */

/*!50003 DROP PROCEDURE IF EXISTS  `Action_Monitoring` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Action_Monitoring`( IN InEmpId VARCHAR(25),In LogInType VARCHAR(25),IN ProId VARCHAR(25),In DivId VARCHAR(25),IN AssigneeId VARCHAR(25) )
BEGIN
SELECT am.actionmainid,aas.actionno,aas.enddate,
(SELECT MAX(b.actionsubid) FROM action_sub b WHERE aas.actionmainid = b.actionassignid ) AS 'subid',
(SELECT c.progress FROM action_sub c  WHERE c.actionassignid = aas.actionassignid AND c.actionsubid = 
(SELECT MAX(b.actionsubid) FROM action_sub b WHERE aas.actionmainid = b.actionassignid) )  AS 'progress',
aas.assignor,aas.assignee,am.projectid 
FROM action_main am  , action_assign aas
WHERE am.actionmainid=aas.actionmainid and aas.enddate BETWEEN '2021-01-01' AND '2021-08-31'
AND CASE WHEN 'P'=LogInType
	THEN 
		CASE WHEN 'A'=ProId
		THEN am.projectid IN ( SELECT pe.projectid FROM project_employee pe WHERE pe.empid=InEmpId )
		ELSE am.projectid=ProId
		END
		
		and 
		
		CASE WHEN 'A'=AssigneeId
		THEN aas.assignee IN (SELECT aas.assignee from action_main am1 ,action_assign aas  WHERE am.actionmainid=aas.actionmainid and am1.projectid = am.projectid )
		ELSE aas.assignee = AssigneeId
		END
			
		
	WHEN 'D' = LogInType
	then
		CASE WHEN 'A'=AssigneeId
		THEN aas.assignee in (select de.empid from division_employee de where de.divisionid=DivId )
		ELSE aas.assignee = AssigneeId
		END
	WHEN 'A' = LogInType
	then
		CASE WHEN 'A'=AssigneeId
		THEN 1=1
		ELSE aas.assignee = AssigneeId
		END
	WHEN 'Z' = LogInType
	THEN
		CASE WHEN 'A'=AssigneeId
		THEN 1=1
		ELSE aas.assignee = AssigneeId
		END
	end
;
END */$$
DELIMITER ;

/* Procedure structure for procedure `Analysis_Demand_Mode` */

/*!50003 DROP PROCEDURE IF EXISTS  `Analysis_Demand_Mode` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Analysis_Demand_Mode`( InProjectId BIGINT(20), InFromDate DATETIME, InToDate DATETIME)
BEGIN
SELECT a.DemandModeName, COUNT(a.DemandNo) AS "DemandCount", SUM(b.EstimatedCost) AS "Amount"  FROM 
demand_main a, demand_booking b 
WHERE  a.DemandNo=b.DemandNo
AND a.ProjectId=InProjectId AND a.DemandDate BETWEEN InFromDate AND InToDate 
GROUP BY DemandModeName;
END */$$
DELIMITER ;

/* Procedure structure for procedure `BriefingList` */

/*!50003 DROP PROCEDURE IF EXISTS  `BriefingList` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `BriefingList`(InLabCode VARCHAR(20),InCommittee VARCHAR(20),InProjectId VARCHAR(20))
BEGIN
	SELECT * FROM	(SELECT  DISTINCT cs.scheduleid, c.committeeid, c.committeeshortname, cs.projectid, 
cs.scheduledate, cs.schedulestarttime, cms.statusdetail,cs.BriefingPaperFrozen, 
cs.MinutesFrozen, cs.meetingid, pm.projectname, pm.projectcode, cs.PresentationFrozen, 
pm.ProjectDirector, pbs.BriefingStatusDesc, pbs.BriefingStatus, pbt.EmpId,
(SELECT COUNT(Remarks) FROM pfms_briefing_transaction trans WHERE trans.ScheduleId=cs.ScheduleId) 
AS rmkCount FROM committee c INNER JOIN committee_schedule cs ON c.committeeid = cs.committeeid 
INNER JOIN project_master pm ON cs.projectid = pm.projectid
 INNER JOIN 
 committee_meeting_status cms ON cms.meetingstatus = cs.scheduleflag INNER JOIN 
 pfms_briefing_status pbs ON pbs.BriefingStatus = cs.BriefingStatus LEFT JOIN pfms_briefing_transaction pbt ON 
 cs.ScheduleId = pbt.ScheduleId AND pbt.BriefingStatus='FWU'  WHERE cms.meetingstatusid >= 7 AND cs.isactive = 1 AND
  c.committeeshortname =InCommittee 
AND cs.labcode =InLabCode AND cs.projectid =InProjectId

UNION

SELECT * FROM (SELECT   cs.scheduleid, c.committeeid, c.committeeshortname, cs.projectid, 
cs.scheduledate, cs.schedulestarttime, cms.statusdetail,cs.BriefingPaperFrozen, 
cs.MinutesFrozen, cs.meetingid, pm.projectname, pm.projectcode, cs.PresentationFrozen, 
pm.ProjectDirector, pbs.BriefingStatusDesc, pbs.BriefingStatus, pbt.EmpId,
(SELECT COUNT(Remarks) FROM pfms_briefing_transaction trans WHERE trans.ScheduleId=cs.ScheduleId) 
AS rmkCount FROM committee c INNER JOIN committee_schedule cs ON c.committeeid = cs.committeeid 
INNER JOIN project_master pm ON cs.projectid = pm.projectid
 INNER JOIN 
 committee_meeting_status cms ON cms.meetingstatus = cs.scheduleflag INNER JOIN 
 pfms_briefing_status pbs ON pbs.BriefingStatus = cs.BriefingStatus LEFT JOIN pfms_briefing_transaction pbt ON 
 cs.ScheduleId = pbt.ScheduleId AND pbt.BriefingStatus='FWU'  WHERE  cs.isactive = 1 AND
  c.committeeshortname =InCommittee 
AND cs.labcode =InLabCode AND cs.projectid =InProjectId AND cs.scheduledate>=CURDATE() order by scheduledate LIMIT 1 ) AS t
) as T1 order by T1.scheduleid desc;
	END */$$
DELIMITER ;

