/* Procedure structure for procedure `CCMCashOutGo_Rpt` */

/*!50003 DROP PROCEDURE IF EXISTS  `CCMCashOutGo_Rpt` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `CCMCashOutGo_Rpt`(IN inProjectId INT, IN inBudgetHeadId INT, inRupees INT(10), IN inFromYear VARCHAR(11), IN inToYear VARCHAR(11), IN inQuarter VARCHAR(2), IN inDbName VARCHAR(20))
BEGIN
    SET @query = CONCAT('
        SELECT
            c.ProjectCode,c.BudgetHeadId,c.FileNo,c.SoNo,c.ItemFor,ROUND(ROUND(IFNULL(cp.PayAmount, 0), 0) / ', inRupees, ', 2) AS PayAmount,cp.PayDate,
            
            CASE
                WHEN ''', inQuarter, ''' = ''Q1'' THEN
                    CASE
                        WHEN cp.PayDate <= CONCAT(', inFromYear, ', ''-06-30'') THEN ''Q1''
                        WHEN cp.PayDate <= CONCAT(', inFromYear, ', ''-09-30'') THEN ''Q2''
                        WHEN cp.PayDate <= CONCAT(', inFromYear, ', ''-12-31'') THEN ''Q3''
                        WHEN cp.PayDate <= CONCAT(', inToYear, ', ''-03-31'') THEN ''Q4''
                        ELSE ''Q5''
                    END
                WHEN ''', inQuarter, ''' = ''Q2'' THEN
                    CASE
                        WHEN cp.PayDate <= CONCAT(', inFromYear, ', ''-09-30'') THEN ''Q2''
                        WHEN cp.PayDate <= CONCAT(', inFromYear, ', ''-12-31'') THEN ''Q3''
                        WHEN cp.PayDate <= CONCAT(', inToYear, ', ''-03-31'') THEN ''Q4''
                        ELSE ''Q5''
                    END
                WHEN ''', inQuarter, ''' = ''Q3'' THEN
                    CASE
                        WHEN cp.PayDate <= CONCAT(', inFromYear, ', ''-12-31'') THEN ''Q3''
                        WHEN cp.PayDate <= CONCAT(', inToYear, ', ''-03-31'') THEN ''Q4''
                        ELSE ''Q5''
                    END
                WHEN ''', inQuarter, ''' = ''Q4'' THEN
                    CASE
                        WHEN cp.PayDate <= CONCAT(', inToYear, ', ''-03-31'') THEN ''Q4''
                        ELSE ''Q5''
                    END
                ELSE ''Q5''
            END AS QtrType,
            b.BudgetHeadDescription
        FROM
            ', inDbName, '.tblcommitmentpay cp,
 
            ', inDbName, '.tblcommitment c,
 
            ', inDbName, '.tblbudgethead b  
        WHERE
            c.ControlNo = cp.ControlNo
            AND c.ProjectId = ', inProjectId, '
            AND c.BudgetHeadId = b.BudgetHeadId
            AND c.BudgetHeadId = ', inBudgetHeadId, '
            AND PayFlag = ''N''
            AND cp.PayAmount > 0
            AND c.IsCancel <> ''Y''
            AND c.IsClosed <> ''Y''
        ORDER BY
