/* Procedure structure for procedure `CCMSummaryView_Rpt` */

/*!50003 DROP PROCEDURE IF EXISTS  `CCMSummaryView_Rpt` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `CCMSummaryView_Rpt`(
    IN inFinYear VARCHAR(11), 
    IN inAsOnDate DATETIME, 
    IN inRupees INT(10), 
    IN inFromYear VARCHAR(11),
    IN inToYear VARCHAR(11), 
    IN inQuarter VARCHAR(2), 
    IN inLoginType VARCHAR(10), 
    IN inEmployeeNo VARCHAR(20),
    IN inDbName VARCHAR(20)
)
BEGIN

/* inQuarter should give Current Quarter For Apr-June=Q1, July-Sep=Q2, Oct-Dec=Q3, Jan-Mar=Q4 */
    SET @query = CONCAT('
        SELECT a.ProjectId, a.ProjectCode, a.BudgetHeadId, b.BudgetHeadDescription, ROUND(a.AllotmentCost/', inRupees, ', 2) AS `AllotmentCost`,
             (
                (SELECT ROUND(IFNULL(SUM(p.Amount), 0)/', inRupees, ', 2) AS amount FROM `', inDbName, '`.tblpayment p WHERE p.AllotmentId = a.AllotmentId AND p.CreatedDate <= ''', inAsOnDate, ''' AND p.BillFlag <> ''R'') +
                (SELECT ROUND(IFNULL(SUM(p.Amount), 0)/', inRupees, ', 2) AS amount FROM `', inDbName, '`.tblotherexpenditure p WHERE p.AllotmentId = a.AllotmentId AND p.CreatedDate <= ''', inAsOnDate, ''') 
            ) AS `Expenditure`,
            (
                (ROUND(a.AllotmentCost/', inRupees, ', 2)) - (
                    (SELECT ROUND(IFNULL(SUM(p.Amount), 0)/', inRupees, ', 2) AS amount FROM `', inDbName, '`.tblpayment p WHERE p.AllotmentId = a.AllotmentId AND p.CreatedDate <= ''', inAsOnDate, ''' AND p.BillFlag <> ''R'') +
                    (SELECT ROUND(IFNULL(SUM(p.Amount), 0)/', inRupees, ', 2) AS amount FROM `', inDbName, '`.tblotherexpenditure p WHERE p.AllotmentId = a.AllotmentId AND p.CreatedDate <= ''', inAsOnDate, ''') 
                )
  ) AS `Balance`,

(SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
WHERE c.ControlNo = cp.ControlNo 
    AND a.ProjectId = c.ProjectId 
    AND a.BudgetHeadId = c.BudgetHeadId 
    AND CASE 
        WHEN ''', inQuarter, ''' = ''Q1'' THEN PayDate <= CONCAT(''', inFromYear, ''', ''-06-30'') AND PayFlag = ''N'' 
        WHEN ''', inQuarter, ''' = ''Q2'' THEN 0 
        WHEN ''', inQuarter, ''' = ''Q3'' THEN 0 
        WHEN ''', inQuarter, ''' = ''Q4'' THEN 0 
        ELSE PayDate <= CONCAT(''', inFromYear, ''', ''-06-30'') AND PayFlag = ''N'' 
        END 
    AND c.IsClosed <> ''Y'' 
    AND c.IsCancel <> ''Y'') AS Q1CashOutGo,
    
(SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
WHERE c.ControlNo = cp.ControlNo 
    AND a.ProjectId = c.ProjectId 
    AND a.BudgetHeadId = c.BudgetHeadId 
    AND CASE 
        WHEN ''', inQuarter, ''' = ''Q3'' THEN 0 
        WHEN ''', inQuarter, ''' = ''Q4'' THEN 0 
        WHEN ''', inQuarter, ''' = ''Q2'' THEN PayDate <= CONCAT(''', inFromYear, ''', ''-09-30'') AND PayFlag = ''N'' 
        ELSE PayDate BETWEEN CONCAT(''', inFromYear, ''', ''-07-01'') AND CONCAT(''', inFromYear, ''', ''-09-30'') AND PayFlag = ''N'' 
        END 
    AND c.IsClosed <> ''Y'' 
    AND c.IsCancel <> ''Y'') AS Q2CashOutGo,
    
(SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
WHERE c.ControlNo = cp.ControlNo 
    AND a.ProjectId = c.ProjectId 
    AND a.BudgetHeadId = c.BudgetHeadId 
    AND CASE 
        WHEN ''', inQuarter, ''' = ''Q4'' THEN 0 
        WHEN ''', inQuarter, ''' = ''Q3'' THEN PayDate <= CONCAT(''', inFromYear, ''', ''-12-31'') AND PayFlag = ''N'' 
        ELSE PayDate BETWEEN CONCAT(''', inFromYear, ''', ''-10-01'') AND CONCAT(''', inFromYear, ''', ''-12-31'') AND PayFlag = ''N'' 
        END 
    AND c.IsClosed <> ''Y'' 
    AND c.IsCancel <> ''Y'') AS Q3CashOutGo,
    
(SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
WHERE c.ControlNo = cp.ControlNo 
    AND a.ProjectId = c.ProjectId 
    AND a.BudgetHeadId = c.BudgetHeadId 
    AND CASE 
        WHEN ''', inQuarter, ''' = ''Q4'' THEN PayDate <= CONCAT(', inToYear, ', ''-03-31'') AND PayFlag = ''N'' 
        ELSE PayDate BETWEEN CONCAT(', inToYear, ', ''-01-01'') AND CONCAT(', inToYear, ', ''-03-31'') AND PayFlag = ''N'' 
        END 
    AND c.IsClosed <> ''Y'' 
    AND c.IsCancel <> ''Y'') AS Q4CashOutGo,

-(
    (
        (SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
        FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
        WHERE c.ControlNo = cp.ControlNo 
            AND a.ProjectId = c.ProjectId 
            AND a.BudgetHeadId = c.BudgetHeadId 
            AND CASE 
                WHEN ''', inQuarter, ''' = ''Q1'' THEN PayDate <= CONCAT(''', inFromYear, ''', ''-06-30'') AND PayFlag = ''N'' 
                WHEN ''', inQuarter, ''' = ''Q2'' THEN 0 
                WHEN ''', inQuarter, ''' = ''Q3'' THEN 0 
                WHEN ''', inQuarter, ''' = ''Q4'' THEN 0 
                ELSE PayDate <= CONCAT(''', inFromYear, ''', ''-06-30'') AND PayFlag = ''N'' 
                END 
            AND c.IsClosed <> ''Y'' 
            AND c.IsCancel <> ''Y'') +
            
        (SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
        FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
        WHERE c.ControlNo = cp.ControlNo 
            AND a.ProjectId = c.ProjectId 
            AND a.BudgetHeadId = c.BudgetHeadId 
            AND CASE 
                WHEN ''', inQuarter, ''' = ''Q3'' THEN 0 
                WHEN ''', inQuarter, ''' = ''Q4'' THEN 0 
                WHEN ''', inQuarter, ''' = ''Q2'' THEN PayDate <= CONCAT(''', inFromYear, ''', ''-09-30'') AND PayFlag = ''N'' 
                ELSE PayDate BETWEEN CONCAT(''', inFromYear, ''', ''-07-01'') AND CONCAT(''', inFromYear, ''', ''-09-30'') AND PayFlag = ''N'' 
                END 
            AND c.IsClosed <> ''Y'' 
            AND c.IsCancel <> ''Y'') +
            
        (SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
        FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
        WHERE c.ControlNo = cp.ControlNo 
            AND a.ProjectId = c.ProjectId 
            AND a.BudgetHeadId = c.BudgetHeadId 
            AND CASE 
                WHEN ''', inQuarter, ''' = ''Q4'' THEN 0 
                WHEN ''', inQuarter, ''' = ''Q3'' THEN PayDate <= CONCAT(''', inFromYear, ''', ''-12-31'') AND PayFlag = ''N'' 
                ELSE PayDate BETWEEN CONCAT(''', inFromYear, ''', ''-10-01'') AND CONCAT(''', inFromYear, ''', ''-12-31'') AND PayFlag = ''N'' 
                END 
            AND c.IsClosed <> ''Y'' 
            AND c.IsCancel <> ''Y'') +
            
        (SELECT ROUND(ROUND(IFNULL(SUM(PayAmount), 0), 0)/', inRupees, ', 2) 
        FROM ', inDbName, '.tblcommitmentpay cp, ', inDbName, '.tblcommitment c 
        WHERE c.ControlNo = cp.ControlNo 
            AND a.ProjectId = c.ProjectId 
            AND a.BudgetHeadId = c.BudgetHeadId 
            AND CASE 
                WHEN ''', inQuarter, ''' = ''Q4'' THEN PayDate <= CONCAT(', inToYear, ', ''-03-31'') AND PayFlag = ''N'' 
                ELSE PayDate BETWEEN CONCAT(', inToYear, ', ''-01-01'') AND CONCAT(', inToYear, ', ''-03-31'') AND PayFlag = ''N'' 
                END 
            AND c.IsClosed <> ''Y'' 
            AND c.IsCancel <> ''Y''
        )
    ) - (
        (ROUND(a.AllotmentCost/', inRupees, ', 2)) - (
            (SELECT ROUND(IFNULL(SUM(p.Amount), 0)/', inRupees, ', 2) AS amount 
            FROM ', inDbName, '.tblpayment p 
            WHERE p.AllotmentId = a.AllotmentId 
                AND p.CreatedDate <= ''', inAsOnDate, ''' 
                AND p.BillFlag <> ''R'') +
                
            (SELECT ROUND(IFNULL(SUM(p.Amount), 0)/', inRupees, ', 2) AS amount 
            FROM ', inDbName, '.tblotherexpenditure p 
            WHERE p.AllotmentId = a.AllotmentId 
                AND p.CreatedDate <= ''', inAsOnDate, ''' ) 
        )
    )
) AS ''Addl(-)/Surr(+)''


 FROM `', inDbName, '`.tblallotment a, `', inDbName, '`.tblbudgethead b, `', inDbName, '`.division_master p
        WHERE a.BudgetHeadId = b.BudgetHeadId 
            AND a.FinancialYear = ''', inFinYear, '''  
            AND a.BudgetHeadId IN (1, 2, 3, 9) 
        GROUP BY a.ProjectID, b.BudgetHeadDescription 
        ORDER BY b.BudgetHeadId, a.ProjectID;



');
PREPARE stmt FROM @query;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
END */$$
DELIMITER ;

