/* Procedure structure for procedure `Dashboard_Approvals` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dashboard_Approvals` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dashboard_Approvals`(IN InEmpid BIGINT(20), IN InLoginType VARCHAR(10))
BEGIN
     
 SELECT
	'DO' AS 'Approvals',	  
	(SELECT COUNT(a.InitiationId)
	FROM pfms_initiation a,division_master b,project_approval g, employee f,employee_desig h,division_master i,employee j	
	WHERE  a.ProjectStatus IN ('PST','ADO','RTD','TCD','DDO','DDD') AND b.DivisionHeadId=InEmpid
	AND a.empid=j.empid AND j.divisionid=b.divisionid AND f.empid=g.empid 
	AND a.approvalid=g.ProjectApprovalId AND f.desigid=h.desigid AND f.divisionid=i.divisionid ) AS "Count" 
	
UNION

SELECT
	'RTMD-DO' AS 'Approvals',
	  
       (SELECT COUNT(a.InitiationId)
	FROM pfms_initiation a,pfms_initiation_approver b,employee f, project_approval g,employee_desig h,division_master i
	WHERE b.EmpId=InEmpid AND a.ProjectStatus IN ('DOR','ADT','TCT','DDT','DTT')
	AND f.empid=g.empid AND a.approvalid=g.ProjectApprovalId AND f.desigid=h.desigid AND 
	f.divisionid=i.divisionid AND b.isactive=1 AND b.type='DO-RTMD') AS "Count"
      
      
      
UNION
SELECT
	'AD' AS 'Approvals',
	(SELECT COUNT(a.InitiationId)
	FROM pfms_initiation a,employee f,project_approval g,employee_desig h,division_master i
	WHERE InEmpid=(SELECT pr.empid FROM pfms_initiation_approver pr WHERE pr.isactive='1' AND pr.type='AD') 
	AND a.ProjectStatus IN ('PRA','RTR','DAA','DAD') AND
	f.empid=g.empid AND a.approvalid=g.ProjectApprovalId AND f.desigid=h.desigid AND f.divisionid=i.divisionid) AS "Count"
	
UNION	
	
SELECT
	'TCM' AS 'Approvals',
	(SELECT COUNT(a.InitiationId)
	FROM pfms_initiation a,lab_master b,employee f,project_approval g, employee_desig h,division_master i
	WHERE a.ProjectStatus IN ('ADR')  AND b.labauthorityid=InEmpid AND f.empid=g.empid AND a.approvalid=g.ProjectApprovalId AND f.desigid=h.desigid
	AND f.divisionid=i.divisionid and b.labcode= (select labcode from employee where empid=InEmpid)) AS "Count"
	
	
UNION 
SELECT
	'Meeting' AS 'Approvals',
	
	(SELECT COUNT(cs.scheduleid)
	FROM committee_schedule cs , committee_member cme, committee_main cma
	WHERE cme.isactive=1 AND cme.labcode IN (SELECT labcode FROM lab_master) 
	AND cma.isactive=1 AND cs.isactive=1
	AND cme.membertype IN ('CC','CS','PS') AND cme.isactive=1
	AND cme.committeemainid =cma.committeemainid AND cme.committeemainid=cs.committeemainid
	AND cs.scheduleflag IN ('MAF','MMF') AND cme.empid=InEmpid) AS "Count"
UNION 
SELECT 
	'Committee' AS 'Approvals',
	(SELECT COUNT(cca.constitutionapprovalid) AS 'Approval'
	FROM committee_constitution_approval cca, employee e,division_master dm   
	WHERE cca.constitutionstatus IN ('CFW','RTR') AND cca.empid=e.empid AND e.divisionid=dm.divisionid AND
	CASE WHEN InLoginType IN ('D','T','J','G') THEN dm.divisionheadid=InEmpid 
	WHEN InLoginType='E' THEN cca.constitutionstatus IN ('RDO','RTDR')
	WHEN InLoginType in  ('Z','C','I') THEN cca.constitutionstatus IN ('RDT','RTDG') END 
	) AS "Count"
 ;  
END */$$
DELIMITER ;

