/* Procedure structure for procedure `Dashboard_Mytask` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dashboard_Mytask` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dashboard_Mytask`(IN InEmpid BIGINT(20))
BEGIN
     
SELECT
	'Actions' AS 'Type',	  
      (SELECT COUNT(a.ActionMainId) FROM action_main a , action_assign aas 
      WHERE aas.Assignee=InEmpid  AND  a.ActionType IN ('N') AND aas.ActionMainId=a.ActionMainId
      AND a.isactive='1' ) AS "All-PDC",
      
	 (SELECT COUNT(a.ActionMainId) FROM action_main a , action_assign aas 
	 WHERE aas.Assignee=InEmpid  AND aas.Assigneelabcode<>'@EXP' AND  aas.actionstatus IN ('A','I','F','B') AND a.ActionType IN ('N') AND aas.ActionMainId=a.ActionMainId AND aas.progress>0
      AND a.isactive='1' ) AS "Pending",
      
      
       (SELECT COUNT(a.ActionMainId) FROM action_main a, action_assign aas 
       WHERE aas.Assignee=InEmpid  AND aas.Assigneelabcode<>'@EXP' AND  aas.actionstatus IN ('A','I','B') AND a.ActionType IN ('N') AND aas.ActionMainId=a.ActionMainId
      AND a.isactive='1' AND aas.enddate=CURDATE()) AS "Today-PDC",
      
      
         (SELECT COUNT(a.ActionMainId) FROM action_main a, action_assign aas 
         WHERE aas.ActionMainId=a.ActionMainId AND aas.Assignee=InEmpid  AND aas.Assigneelabcode<>'@EXP' AND aas.actionstatus IN ('A','I','B') AND a.ActionType IN ('N')
      AND a.isactive='1' AND aas.enddate BETWEEN DATE_ADD(CURDATE(), INTERVAL 1 DAY ) AND DATE_ADD(CURDATE(), INTERVAL 6 DAY ) ) AS "Upcoming",
      
       (SELECT COUNT(a.ActionMainId) FROM action_main a, action_assign aas,employee e WHERE  aas.ActionMainId=a.ActionMainId
      AND aas.Assignor=InEmpid  AND aas.Assigneelabcode<>'@EXP' AND aas.actionstatus IN ('F') AND a.ActionType IN ('N') AND aas.Assignee = e.empid AND e.isactive<>0
      AND  a.isactive='1' ) AS "Review"
      
      
UNION
	 SELECT
	'Meeting' AS 'Type',
	  
      (SELECT COUNT(a.ActionMainId) FROM action_main a , action_assign aas 
      WHERE aas.Assignee=InEmpid  AND  a.ActionType IN ('S') AND aas.ActionMainId=a.ActionMainId
      AND a.isactive='1' ) AS "All-PDC",
      
	 (SELECT COUNT(a.ActionMainId) FROM action_main a, action_assign aas WHERE  aas.ActionMainId=a.ActionMainId
       AND aas.Assignee=InEmpid  AND aas.Assigneelabcode<>'@EXP' AND aas.actionstatus IN ('A','I','F','B') AND a.ActionType IN ('S') AND aas.progress>0
      AND a.isactive='1' ) AS "Pending",
      
      
