/* Procedure structure for procedure `Dms_DakAssignedByMeList` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dms_DakAssignedByMeList` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakAssignedByMeList`(InEmpId BIGINT(20), InFromDate VARCHAR(20), InToDate VARCHAR(20),InSelEmpId BIGINT(20))
BEGIN
SELECT a.DakId,a.DakNo,c.EmpName,e.SourceShortName,
  CASE 
        WHEN a.ProjectType = 'P' THEN p.Projectcode
        WHEN a.ProjectType = 'N' THEN n.NonShortName
        WHEN a.ProjectType = 'O' THEN o.ProjectCode
        ELSE 'NA' END AS 'Projectcode',a.RefNo,a.Refdate,d.Designation,g.ActionRequired,f.statusdesc,
       IFNULL(CountOfAllMarkers, 0) AS CountOfAllMarkers,
       IFNULL(CountOfActionMarkers, 0) AS CountOfActionMarkers,
       IFNULL(CountOfMarkersAck, 0) AS CountOfMarkersAck,
       IFNULL(CountOfActMarkersReply, 0) AS CountOfActMarkersReply,a.dakstatus
  FROM dak a
       JOIN dak_assign b ON a.DakId = b.DakId AND (CASE WHEN InSelEmpId = 0 THEN 1=1 ELSE InSelEmpId = b.EmpId END)
       JOIN employee c ON b.EmpId=c.EmpId
       JOIN employee_desig d ON c.DesigId=d.DesigId
       JOIN dak_source_details e ON a.sourcedetailid = e.sourcedetailid
       JOIN dak_status f ON a.dakstatus = f.dakstatus
       JOIN dak_action g ON a.ActionId = g.ActionId
       JOIN dak_marking h ON b.DakMarkingId=h.DakMarkingId AND h.EmpId=InEmpId
       LEFT  JOIN project_master p     ON p.ProjectId = a.ProjectId AND a.ProjectType = 'P'
       LEFT  JOIN dak_non_project n    ON n.NonProjectId = a.ProjectId AND a.ProjectType = 'N'
       LEFT JOIN dak_others_project o ON o.ProjectOtherId = a.ProjectId AND a.ProjectType = 'O'
     LEFT JOIN (SELECT m.DakId,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1' THEN m.DakMarkingId END) AS CountOfAllMarkers,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1'  THEN m.DakMarkingId END) AS CountOfActionMarkers,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1' AND m.DakAckStatus = 'Y' THEN m.DakMarkingId END) AS CountOfMarkersAck
     FROM dak_marking m
     GROUP BY DakId
     ) AS marking_counts ON a.dakid = marking_counts.DakId

    LEFT JOIN (
    SELECT r.dakid,
        COUNT(DISTINCT DakReplyId) AS CountOfActMarkersReply
    FROM dak_reply r
    JOIN dak_marking m ON r.dakid = m.dakid AND r.EmpId = m.EmpId
    WHERE IsActive = '1'
    GROUP BY dakid
    ) AS reply_counts ON a.dakid = reply_counts.dakid 
 WHERE a.dakstatus IN ('DI','DD','DA','DR','RP','RM','AP') and (a.ReceiptDate BETWEEN InFromDate AND InToDate) 
 ORDER BY DakId DESC;
 END */$$
DELIMITER ;

