  AND a.ActionId=2 AND DakStatus NOT IN('DI') AND a.ReceiptDate BETWEEN InFromDate AND InToDate) END) AS TotalRepliedInTime ,   
(SELECT CASE  WHEN (SELECT logintypeDms FROM login WHERE username=InUsername) IN ('A','Z','E','V') THEN
