AND DakStatus NOT IN('DI') AND a.ReceiptDate BETWEEN InFromDate AND InToDate)
 ELSE
(SELECT COUNT(*) FROM dak a WHERE a.DakId IN (SELECT DakId FROM dak_assign_reply WHERE a.CreatedDate>ActionDueDate AND ((InEmpId = 'All')OR (EmpId=InEmpId))) 
 AND a.ActionId=2 AND DakStatus NOT IN('DI') AND a.ReceiptDate BETWEEN InFromDate AND InToDate) END)AS TotalRepliedOutTime,
 (SELECT CASE  WHEN (SELECT logintypeDms FROM login WHERE username=InUsername) IN ('A','Z','E','V') THEN
(SELECT COUNT(*) FROM dak a WHERE a.DakId IN (SELECT DakId FROM dak_assign WHERE a.DakId=DakId AND IsActive=1 AND ((InEmpId = 'All')OR (EmpId=InEmpId)))AND a.DakId NOT IN 
(SELECT DakId FROM dak_assign_reply WHERE a.DakId=DakId AND ((InEmpId = 'All')OR (EmpId=InEmpId))) AND a.ActionId = 2 AND a.DakStatus NOT IN ('DI') AND a.ReceiptDate BETWEEN InFromDate AND InToDate)
   ELSE
(SELECT COUNT(*) FROM dak a WHERE a.DakId IN (SELECT DakId FROM dak_assign WHERE a.DakId=DakId AND ((InEmpId = 'All')OR (EmpId=InEmpId)) AND IsActive=1)AND a.DakId NOT IN 
(SELECT DakId FROM dak_assign_reply WHERE a.DakId=DakId AND ((InEmpId = 'All')OR (EmpId=InEmpId))) AND a.ActionId = 2 AND a.DakStatus NOT IN ('DI') AND a.ReceiptDate BETWEEN InFromDate AND InToDate) END)AS TotalNotReplied;
   
