/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakAssignedListToMe`(InEmpId BIGINT(20),InFromDate VARCHAR(20), InToDate VARCHAR(20),InSelEmpId BIGINT(20))
BEGIN
SELECT a.DakNo,d.SourceShortName,a.RefNo,b.ActionRequired,c.remarks,a.subject,a.Refdate,a.DakId,c.DakAssignId,c.ReplyStatus,
CASE WHEN a.ProjectType = 'P' THEN (SELECT p.Projectcode FROM project_master p WHERE a.ProjectId = p.ProjectId) WHEN a.ProjectType = 'N' 
THEN (SELECT n.NonShortName FROM dak_non_project n WHERE a.ProjectId = n.NonProjectId) WHEN a.ProjectType = 'O' THEN 
(SELECT o.ProjectCode FROM dak_others_project o WHERE a.ProjectId = o.ProjectOtherId) ELSE 'NA' END AS 'Projectcode',
m.EmpId,e.EmpName,f.Designation,s.statusdesc,
(SELECT COUNT(m.DakMarkingId) FROM  dak_marking m WHERE m.DakId=a.dakid AND m.IsActive='1') AS CountOfAllMarkers, 
     (SELECT COUNT(m.DakMarkingId) FROM  dak_marking m WHERE m.DakId=a.dakid AND m.IsActive='1') AS CountOfActionMarkers,            
     (SELECT COUNT(m.DakMarkingId) FROM  dak_marking m WHERE m.DakId=a.dakid AND m.DakAckStatus='Y' AND m.IsActive='1' ) AS CountOfMarkersAck,
     (SELECT CASE WHEN EXISTS (SELECT r.DakReplyId FROM dak_reply r WHERE r.DakId = a.dakid)
             THEN (SELECT COUNT(r.DakReplyId) FROM dak_reply r,dak_marking m  WHERE a.dakid = r.dakid AND m.dakid=r.dakid AND  r.EmpId=m.EmpId AND m.IsActive='1')
                 ELSE 0 END )  AS CountOfActMarkersReply,a.dakstatus,
                 (select count(s.DakAssignId) from dak_assign_reply s where a.DakId=s.DakId) as 'AssignReplyCount',a.ClosingAuthority,
                 (SELECT COUNT(en.DakId) FROM dak_enote en WHERE a.DakId=en.DakId) AS eNoteDakIdCount
 FROM dak a,dak_action b,dak_assign c,dak_source_details d,dak_marking m,employee e,
employee_desig f,dak_status s WHERE a.ActionId=b.ActionId AND a.DakId=c.DakId AND a.dakstatus=s.dakstatus AND m.EmpId=e.EmpId AND 
c.DakMarkingId=m.DakMarkingId AND e.DesigId=f.DesigId  AND a.SourceDetailId=d.SourceDetailId AND c.IsActive='1' AND c.EmpId=InEmpId AND  (CASE WHEN InSelEmpId = 0 THEN 1=1 ELSE InSelEmpId = m.EmpId END) AND 
a.dakstatus IN ('DD','DA','DR','RP','FP','RM') and (a.ReceiptDate BETWEEN InFromDate AND InToDate) ORDER BY DakId DESC;
END */$$
DELIMITER ;

