/* Procedure structure for procedure `Dms_DakClosedList` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dms_DakClosedList` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakClosedList`(InFromDate VARCHAR(20), InToDate VARCHAR(20),InStatusValue VARCHAR(20), InLoginType VARCHAR(1), InUsername VARCHAR(20), InEmpId BIGINT(20),InLetterTypeId VARCHAR(20),InPriorityid VARCHAR(20),InSourcedetailid VARCHAR(20),InSourceId VARCHAR(20),InProjectType VARCHAR(20),InProjectId VARCHAR(20),InMemberTyPeId VARCHAR(20),InEmployeeId VARCHAR(20),InDivisionCode VARCHAR(50),InLabCode VARCHAR(50))
BEGIN
SELECT Distinct a.dakid,b.lettertype,c.priorityname,d.sourceshortname,a.Refno,a.dakstatus,a.Refdate,e.statusdesc,a.dakno,
g.ActionRequired,a.ActionDueDate,a.sourceid,ClosedDate,a.subject ,a.ProjectType, a.ProjectId,
   CASE 
        WHEN a.ProjectType = 'P' THEN p.Projectcode
        WHEN a.ProjectType = 'N' THEN n.NonShortName
        WHEN a.ProjectType = 'O' THEN o.ProjectCode
        ELSE 'NA' END AS 'Projectcode',a.ActionId,a.ClosedBy,a.ClosingAuthority,la.EmpName AS 'ClosedByName'
  FROM dak a
     JOIN dak_letter_type b ON a.lettertypeid = b.lettertypeid
     JOIN dak_priority c ON a.priorityid = c.priorityid
     JOIN dak_source_details d ON a.sourcedetailid = d.sourcedetailid
     JOIN dak_status e ON a.dakstatus = e.dakstatus
     JOIN dak_action g ON a.ActionId = g.ActionId
     JOIN dak_delivery_type j ON a.DeliveryTypeId = j.DeliveryTypeId
     JOIN dak_source s ON a.SourceId = s.SourceId
     LEFT JOIN project_master p     ON p.ProjectId = a.ProjectId AND a.ProjectType = 'P'
     LEFT JOIN dak_non_project n    ON n.NonProjectId = a.ProjectId AND a.ProjectType = 'N'
     LEFT JOIN dak_others_project o ON o.ProjectOtherId = a.ProjectId AND a.ProjectType = 'O'
     LEFT JOIN login AS l ON a.ClosedBy = l.username
     LEFT JOIN employee AS la ON l.EmpId = la.EmpId
      LEFT JOIN dak_marking m ON a.DakId = m.DakId
      
     WHERE  a.dakstatus IN ('DC')  and a.LabCode=InLabCode
     /*to provide closed List for both 'A','Z','E','V' and also for users */
     AND (
     (InLoginType IN ('A','Z','E','V'))
        OR
        (EXISTS (SELECT 1 FROM dak_marking m WHERE m.dakid = a.dakid AND m.EmpId = InEmpId AND m.IsActive='1'))
     )
     AND(a.ReceiptDate BETWEEN InFromDate AND InToDate) 
     AND (
            (InStatusValue = 'All')
            OR (InStatusValue = 'Completed' AND a.DakStatus = 'DC' AND a.ActionDueDate <= a.ClosedDate)
            OR (InStatusValue = 'CompletedDelay' AND a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate)
        )
   AND 
    (
	(InLetterTypeId = 'All') OR (InLetterTypeId = a.DeliveryTypeId)
    )
    AND (
        (InPriorityid = 'All') OR (InPriorityid = a.priorityid)
    )
    AND (
	(InSourcedetailid = 'All') OR (InSourcedetailid = a.sourcedetailid)
    )
    AND (
        (InSourceId = 'All') OR (InSourceId = a.SourceId)
    )
    AND (
        (InProjectType = 'All') OR (InProjectType = a.ProjectType)
    )
    AND (
        (InProjectId = 'All') OR (InProjectId = a.ProjectId)
    )
    AND (
        (InMemberTyPeId = 'All') OR (InMemberTyPeId = m.DakMemberTypeId)
    )
    AND (
        (InEmployeeId = 'All') OR (InEmployeeId = m.EmpId)
    )
    AND (
         (SELECT logintypeDms FROM login WHERE username = InUsername) IN ('A','Z','E','V') OR a.DivisionCode = InDivisionCode
    )
  ORDER BY ClosedDate DESC;
END */$$
DELIMITER ;

