/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakCSWReplyModalList`(InDakId BIGINT(20), InEmpId BIGINT(20), InUsername VARCHAR(20), InDakAdmin VARCHAR(20))
BEGIN

SELECT
  r.DakAssignReplyId,
  r.EmpId AS 'AssignReplyEmpId',
  r.Reply,
  r.DakId,
  r.CreatedBy,
  r.CreatedDate,
  e.EmpName,
  d.Designation,
 
  (SELECT COUNT(a.DakAssignReplyAttachmentId) FROM dak_assign_reply_attachment a WHERE a.DakAssignReplyId = r.DakAssignReplyId) AS 'DakAssignReplyAttachCount',
  (SELECT  CASE WHEN b.loginTypeDms IN ('E','L','V')  OR b.Username = InDakAdmin THEN 'ReplyForward' ELSE 'NA' END) AS ReplyAction,
  r.DakAssignId,m.DakMarkingId, m.EmpId AS 'MarkedEmpId',
  (SELECT g.EmpName FROM employee g WHERE g.EmpId=m.EmpId) AS'MarkedEmpName',
  (SELECT h.Designation FROM employee g,employee_desig h WHERE g.EmpId=m.EmpId AND g.DesigId=h.DesigId) AS'MarkedEmpDesig',
  a.DakStatus,
  (SELECT COUNT(r.DakAssignReplyId) FROM dak_assign_reply r  WHERE a.dakid = r.dakid) AS replyCWCountOverall,r.ReplyStatus
  
        
FROM dak_assign_reply r,dak_assign f,dak_marking m, login b , employee e, employee_desig d , dak a
WHERE r.DakId = InDakId AND r.DakId=a.DakId AND f.DakAssignId=r.DakAssignId AND f.DakMarkingId=m.DakMarkingId  AND r.EmpId=e.EmpId  AND e.DesigId=d.DesigId AND b.Username=InUsername AND b.EmpId = InEmpId 
AND (
    CASE
       WHEN b.loginTypeDms IN ('A','Z','E','V') OR b.Username = InDakAdmin OR (SELECT z.EmpId FROM dak_marking z,dak_assign s WHERE s.DakAssignId=r.DakAssignId AND z.DakMarkingId=s.DakMarkingId )=InEmpId
       THEN 1
      ELSE r.EmpId = InEmpId
    END
  )ORDER BY r.DakAssignReplyId;
  

END */$$
DELIMITER ;

