/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakDashBoardCount`(InEmpId BIGINT(20),InFromDate VARCHAR(20), InToDate VARCHAR(20),InUsername VARCHAR(20), InTypeValue  VARCHAR(20),InProjectType  VARCHAR(20),InSourceTypeId  VARCHAR(20),InProjectTypeId  VARCHAR(20),InMemberTypeId  VARCHAR(20),InEmployeeId VARCHAR(20),InDeliveryTypeId  VARCHAR(20),InPriorityId VARCHAR(20))
BEGIN
