  ((InMemberTypeId= 'All')OR ( InMemberTypeId IN (SELECT m.DakMemberTypeId FROM dak_marking m WHERE m.DakId=b.DakId) )) AND ((InEmployeeId= 'All')OR ( InEmployeeId IN (SELECT m.EmpId FROM dak_marking m WHERE m.DakId=b.DakId) ))) END) AS DakRecordsCount,

(SELECT CASE WHEN (SELECT logintypeDms FROM login WHERE username = InUsername) IN ('A', 'Z', 'E', 'V') THEN
   (SELECT COUNT(*) FROM dak a WHERE a.ActionId='2' AND a.IsActive='1' AND a.ReceiptDate BETWEEN InFromDate AND InToDate AND 
   ((InTypeValue = 'All')OR (a.SourceId = InTypeValue)) AND ((InProjectType = 'All')OR (a.ProjectType=InProjectType)) AND ((InSourceTypeId = 'All')OR (a.SourceDetailId=InSourceTypeId)) AND ((InProjectTypeId = 'All')OR (a.ProjectId=InProjectTypeId))
   AND ((InDeliveryTypeId = 'All')OR (a.DeliveryTypeId = InDeliveryTypeId)) AND ((InPriorityId = 'All')OR (a.PriorityId=InPriorityId))  AND  ((InMemberTypeId = 'All')OR ( InMemberTypeId IN (SELECT m.DakMemberTypeId FROM dak_marking m WHERE m.DakId=a.DakId) ) )AND ((InEmployeeId= 'All')OR ( InEmployeeId IN (SELECT m.EmpId FROM dak_marking m WHERE m.DakId=a.DakId) )))
    ELSE
(SELECT COUNT(*) FROM dak_marking a, dak b WHERE b.ReceiptDate BETWEEN InFromDate AND InToDate AND
((InTypeValue = 'All')OR (b.SourceId = InTypeValue)) AND ((InProjectType = 'All')OR (b.ProjectType=InProjectType)) AND ((InSourceTypeId = 'All')OR (b.SourceDetailId=InSourceTypeId)) AND ((InProjectTypeId = 'All')OR (b.ProjectId=InProjectTypeId)) AND
 a.ActionId='2' AND a.DakId = b.DakId AND a.isActive = '1' AND a.EmpId = InEmpId AND ((InDeliveryTypeId = 'All')OR (b.DeliveryTypeId = InDeliveryTypeId)) AND ((InPriorityId = 'All')OR (b.PriorityId=InPriorityId)) AND 
