(SELECT CASE  WHEN (SELECT logintypeDms FROM login WHERE username=InUsername) IN ('A','Z','E','V') THEN 
(SELECT COUNT(*) FROM dak a WHERE a.DakStatus NOT IN('DI')  AND a.IsActive = '1' AND a.ActionId='2' AND a.ReceiptDate BETWEEN InFromDate AND InToDate AND 
((InTypeValue = 'All')OR (a.SourceId = InTypeValue)) AND ((InProjectType = 'All')OR (a.ProjectType=InProjectType)) AND ((InSourceTypeId = 'All')OR (a.SourceDetailId=InSourceTypeId)) AND ((InProjectTypeId = 'All')OR (a.ProjectId=InProjectTypeId))
AND  ((InDeliveryTypeId = 'All')OR (a.DeliveryTypeId = InDeliveryTypeId)) AND ((InPriorityId = 'All')OR (a.PriorityId=InPriorityId))  AND ((InMemberTypeId = 'All')OR ( InMemberTypeId IN (SELECT m.DakMemberTypeId FROM dak_marking m WHERE m.DakId=a.DakId) ) )AND ((InEmployeeId= 'All')OR ( InEmployeeId IN (SELECT m.EmpId FROM dak_marking m WHERE m.DakId=a.DakId) ))) 
 ELSE 
 (SELECT  COUNT(*) FROM dak_marking a JOIN  dak b ON  a.DakId = b.DakId  WHERE  b.DakStatus NOT IN('DI') AND b.ActionId='2' AND b.ReceiptDate BETWEEN InFromDate AND InToDate AND
 ((InTypeValue = 'All')OR (b.SourceId = InTypeValue)) AND ((InProjectType = 'All')OR (b.ProjectType=InProjectType)) AND ((InSourceTypeId = 'All')OR (b.SourceDetailId=InSourceTypeId)) AND ((InProjectTypeId = 'All')OR (b.ProjectId=InProjectTypeId))
