DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakDetailedList`(InFromDate VARCHAR(20), InToDate VARCHAR(20),InStatusValue VARCHAR(20),InLetterTypeId VARCHAR(20),InPriorityid VARCHAR(20),InSourcedetailid VARCHAR(20),InSourceId VARCHAR(20),InProjectType VARCHAR(20),InProjectId VARCHAR(20),InMemberTyPeId VARCHAR(20),InEmpId VARCHAR(20),InDivisionCode VARCHAR(50),InLabCode VARCHAR(50),InUserName VARCHAR(50))
BEGIN
SELECT DISTINCT a.dakid, b.lettertype, c.priorityname, d.sourceshortname, a.Refno, a.dakstatus, a.Refdate, e.statusdesc, a.dakno,
 g.ActionRequired, a.ActionDueDate, a.sourceid, a.ClosedDate,
 CASE 
     WHEN a.DakStatus = 'DC' AND a.ActionDueDate <= a.ClosedDate   THEN 'CO'
     WHEN a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate   THEN 'CD' 
     WHEN a.DakStatus <> 'DC' AND CURDATE() <= a.ActionDueDate    THEN 'OG'
     WHEN a.DakStatus <> 'DC' AND CURDATE() > a.ActionDueDate     THEN 'DO'
     ELSE 'NA' 
  END AS statusFlag,
     a.subject, a.ProjectType, a.ProjectId,a.DistributedDate,
    CASE 
        WHEN a.ProjectType = 'P' THEN p.Projectcode
        WHEN a.ProjectType = 'N' THEN n.NonShortName
        WHEN a.ProjectType = 'O' THEN o.ProjectCode
        ELSE 'NA' END AS 'Projectcode',
     a.ActionId,a.ClosedBy,a.priorityId,a.Signatory,a.DeliveryTypeId, a.ApprovedBy, a.DirectorApproval,a.ClosingAuthority,
     IFNULL(CountOfAllMarkers, 0) AS CountOfAllMarkers,
     IFNULL(CountOfActionMarkers, 0) AS CountOfActionMarkers,
     IFNULL(CountOfMarkersAck, 0) AS CountOfMarkersAck,
     IFNULL(CountOfActMarkersReply, 0) AS CountOfActMarkersReply                    
     FROM dak a
      JOIN dak_letter_type b ON a.lettertypeid = b.lettertypeid
      JOIN dak_priority c ON a.priorityid = c.priorityid
      JOIN dak_source_details d ON a.sourcedetailid = d.sourcedetailid
      JOIN dak_status e ON a.dakstatus = e.dakstatus
      JOIN dak_action g ON a.ActionId = g.ActionId
      JOIN dak_source s ON a.SourceId = s.SourceId
      JOIN dak_delivery_type t ON a.DeliveryTypeId=t.DeliveryTypeId
     LEFT  JOIN project_master p     ON p.ProjectId = a.ProjectId AND a.ProjectType = 'P'
     LEFT  JOIN dak_non_project n    ON n.NonProjectId = a.ProjectId AND a.ProjectType = 'N'
     LEFT JOIN dak_others_project o ON o.ProjectOtherId = a.ProjectId AND a.ProjectType = 'O'
     LEFT JOIN dak_marking m ON a.DakId = m.DakId
     
     LEFT JOIN (SELECT m.DakId,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1' THEN m.DakMarkingId END) AS CountOfAllMarkers,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1' /*AND m.MarkerAction = 'A'*/ THEN m.DakMarkingId END) AS CountOfActionMarkers,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1' AND m.DakAckStatus = 'Y' THEN m.DakMarkingId END) AS CountOfMarkersAck
     FROM dak_marking m
     GROUP BY DakId
     ) AS marking_counts ON a.dakid = marking_counts.DakId

    LEFT JOIN (
    SELECT r.dakid,
        COUNT(DISTINCT DakReplyId) AS CountOfActMarkersReply
    FROM dak_reply r
    JOIN dak_marking m ON r.dakid = m.dakid AND r.EmpId = m.EmpId
    WHERE IsActive = '1' /*AND MarkerAction = 'A'*/
    GROUP BY dakid
    ) AS reply_counts ON a.dakid = reply_counts.dakid

    WHERE (a.ReceiptDate BETWEEN InFromDate AND InToDate) AND  a.dakstatus IN ('DI','DD','DA','DR','RP','FP','RM','AP')  and a.LabCode=InLabCode
    AND (
            (InStatusValue = 'All')
            OR (InStatusValue = 'Ongoing' AND a.DakStatus <> 'DC' AND CURDATE() <= a.ActionDueDate)
            OR (InStatusValue = 'OngoingDelay' AND a.DakStatus <> 'DC' AND CURDATE() > a.ActionDueDate)
            OR (InStatusValue = 'Completed' AND a.DakStatus = 'DC' AND a.ActionDueDate <= a.ClosedDate)
            OR (InStatusValue = 'CompletedDelay' AND a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate)
            OR (InStatusValue = 'Urgent' AND a.PriorityId = '3')
        )
    AND (
        (InLetterTypeId = 'All') OR (InLetterTypeId = a.DeliveryTypeId)
    )
    AND (
        (InPriorityid = 'All') OR (InPriorityid = a.priorityid)
    )
    AND (
	(InSourcedetailid = 'All') OR (InSourcedetailid = a.sourcedetailid)
    )
    AND (
        (InSourceId = 'All') OR (InSourceId = a.SourceId)
    )
    AND (
        (InProjectType = 'All') OR (InProjectType = a.ProjectType)
    )
    AND (
        (InProjectId = 'All') OR (InProjectId = a.ProjectId)
    )
    AND (
        (InMemberTyPeId = 'All') OR (InMemberTyPeId = m.DakMemberTypeId)
    )
    AND (
        (InEmpId = 'All') OR (InEmpId = m.EmpId)
    )
    AND (
         (SELECT logintypeDms FROM login WHERE username = InUserName) IN ('A','Z','E','V') OR a.DivisionCode = InDivisionCode
    )

  ORDER BY dakid DESC;
END */$$
DELIMITER ;

