      WHEN b.DakAckStatus = 'Y' AND NOT EXISTS (SELECT 1 FROM dak_reply r WHERE b.DakId = r.DakId AND b.EmpId = r.EmpId AND r.ReplyStatus = 'R') THEN 'Acknowledged'
      WHEN EXISTS (SELECT 1 FROM dak_reply r WHERE b.DakId = r.DakId AND b.EmpId = r.EmpId AND r.ReplyStatus = 'R') THEN 'Replied'
      ELSE 'Distributed'
   END AS 'Status',
   (SELECT r.DakReplyId FROM dak_reply r WHERE b.DakId = r.DakId AND b.EmpId = r.EmpId AND r.ReplyStatus = 'R') AS 'DakReplyId',
   b.MsgType,b.IsActive,a.DakId,a.DakStatus,b.MarkerAction,
   (SELECT CASE WHEN EXISTS (SELECT s.DakAssignId FROM dak_assign_reply s,dak_assign t,dak_marking g  WHERE s.DakId = a.dakId AND s.DakAssignId=t.DakAssignId AND g.DakMarkingId=t.DakMarkingId AND g.EmpId=b.EmpId  )THEN (SELECT COUNT(s.DakAssignId) FROM dak_assign_reply s,dak_assign t,dak_marking g  WHERE s.DakId = a.dakid AND s.DakAssignId=t.DakAssignId AND g.DakMarkingId=t.DakMarkingId AND g.EmpId=b.EmpId )ELSE 0 END ) AS CountOfDakAssignReplyId,
   g.ActionRequired
    
    FROM dak a, dak_marking b, employee c,dak_action g
    WHERE
        a.DakId = b.DakId AND (a.ReceiptDate BETWEEN InFromDate AND InToDate) AND a.dakstatus IN ('DD', 'DA', 'CA', 'DR', 'RM', 'RP', 'AP', 'DC') AND b.EmpId = c.EmpId and  a.ActionId=g.ActionId and b.IsActive='1'
    ORDER BY
        dakid DESC,DakMarkingId ;
END */$$
DELIMITER ;

