/* Procedure structure for procedure `Dms_DakFilter` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dms_DakFilter` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakFilter`(IN FilterType VARCHAR(15),IN DetailId BIGINT(20),IN FromDate VARCHAR(11),IN ToDate VARCHAR(11),IN LoginType VARCHAR(10),IN EmpId bigint(10),IN UserName VARCHAR(40))
BEGIN
SELECT a.dakId,b.lettertype, c.priorityname, d.sourceshortname, a.Refno, a.dakstatus, a.Refdate, e.statusdesc, a.dakno,a.subject,f.ActionRequired,a.ActionId,a.ActionDueDate,a.SourceId,ClosedDate, 
(SELECT CASE WHEN EXISTS (SELECT 1 FROM dak_reply r WHERE r.DakId = a.dakid )THEN (SELECT COUNT(r.DakReplyId) FROM dak_reply r  WHERE a.dakid = r.dakid ) ELSE 0 END ) AS 'replyEditStatus' ,
 CASE WHEN a.ProjectType = 'P' THEN (SELECT p.Projectcode FROM project_master p WHERE a.ProjectId = p.ProjectId) WHEN a.ProjectType = 'N' THEN (SELECT n.NonShortName FROM dak_non_project n WHERE a.ProjectId = n.NonProjectId)  WHEN a.ProjectType = 'O' THEN (SELECT o.ProjectCode FROM dak_others_project o WHERE a.ProjectId = o.ProjectOtherId) ELSE 'NA' END AS 'Projectcode'
FROM dak a,dak_letter_type b, dak_priority c,dak_source_details d,dak_status e,dak_action f 
WHERE
case when FilterType='Source' AND (a.ReceiptDate BETWEEN FromDate AND ToDate) then (case when DetailId=-1 then 1=1 else a.SourceDetailId=DetailId end )
     WHEN FilterType='Project' and a.ProjectType='P' AND (a.ReceiptDate BETWEEN FromDate AND ToDate) THEN (CASE WHEN DetailId=-1 THEN 1=1 ELSE a.ProjectId=DetailId END )
     WHEN FilterType='NonProject' AND a.ProjectType='N' AND (a.ReceiptDate BETWEEN FromDate AND ToDate)  THEN (CASE WHEN DetailId=-1 THEN 1=1 ELSE a.ProjectId=DetailId END )
     WHEN FilterType='OtherProject' AND a.ProjectType='O' AND (a.ReceiptDate BETWEEN FromDate AND ToDate)  THEN (CASE WHEN DetailId=-1 THEN 1=1 ELSE a.ProjectId=DetailId END ) end
     
and a.lettertypeid=b.lettertypeid AND a.priorityid=c.priorityid AND a.sourcedetailid=d.sourcedetailid 
AND a.dakstatus=e.dakstatus AND a.ActionId = f.ActionId and (CASE WHEN LoginType='A' || LoginType='Z' || LoginType='E' || LoginType='L' THEN 1=1 ELSE a.CreatedBy=UserName OR 
(EXISTS (SELECT 1 FROM dak_marking m WHERE m.dakid = a.dakid AND m.EmpId = EmpId AND m.IsActive='1')) END ) and a.DakStatus not in('DI')
 ORDER BY Dakid DESC;
END */$$
DELIMITER ;

/* Procedure structure for procedure `Dms_DakGroupCardsCount` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dms_DakGroupCardsCount` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakGroupCardsCount`(InFromDate VARCHAR(20), InToDate VARCHAR(20))
BEGIN
SELECT
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y') AS AllGroupDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupRecordsDakCount FROM dak a  LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.ActionId = 1 AND a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y') AS AllGroupRecordsDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupActionDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.ActionId = 2 AND a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y') AS AllGroupActionDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupDistributedDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.ActionId = 2 AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND a.DakStatus NOT IN ('DI'))AS AllGroupDistributedDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupPendingReplyDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.ActionId = 2 AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND a.DakStatus IN ('DD','DA') AND NOT EXISTS (SELECT 1 FROM dak_reply WHERE dak_reply.dakId = a.dakId))AS AllGroupPendingReplyDakCount,
    (SELECT COUNT(DISTINCT b.DakId) FROM dak b LEFT JOIN dak_reply a ON a.DakId = b.DakId LEFT JOIN dak_marking mark ON mark.DakId = b.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.replystatus = 'R' AND b.ActionId = '2' AND b.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y') AS AllGroupRepliedCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupPncDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.dakstatus IN ('DD','DA','DR')  AND a.ClosingAuthority='P' AND a.ActionId='2' AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y')AS AllGroupPncDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupDirectorDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.dakstatus IN ('RM','RP','FP')  AND a.DirectorApproval='R' AND a.ActionId='2' AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y')AS AllGroupDirectorDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupClosedDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.dakstatus IN ('DC')  AND a.ActionId='2' AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y')AS AllGroupClosedDakCount;
  
END */$$
DELIMITER ;

