/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakGroupDashBoardCount`(InDakMemberTypeId  VARCHAR(20),InFromDate VARCHAR(20), InToDate VARCHAR(20))
BEGIN
SELECT DISTINCT
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId) AS AllGroupDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupRecordsDakCount FROM dak a  LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.ActionId = 1 AND a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId) AS AllGroupRecordsDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupActionDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.ActionId = 2 AND a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId) AS AllGroupActionDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupDistributedDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.ActionId = 2 AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND a.DakStatus NOT IN ('DI') AND mark.DakMemberTypeId = InDakMemberTypeId)AS AllGroupDistributedDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupPendingReplyDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.ActionId = 2 AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND a.DakStatus IN ('DD','DA') AND NOT EXISTS (SELECT 1 FROM dak_reply WHERE dak_reply.dakId = a.dakId) AND mark.DakMemberTypeId = InDakMemberTypeId)AS AllGroupPendingReplyDakCount,
    (SELECT COUNT(DISTINCT b.DakId) FROM dak b LEFT JOIN dak_reply a ON a.DakId = b.DakId LEFT JOIN dak_marking mark ON mark.DakId = b.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.replystatus = 'R' AND b.ActionId = '2' AND b.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId) AS AllGroupRepliedCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupPncDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId WHERE a.dakstatus IN ('DD','DA','DR')  AND a.ClosingAuthority='P' AND a.ActionId='2' AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId)AS AllGroupPncDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupDirectorDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.dakstatus IN ('RM','RP','FP')  AND a.DirectorApproval='R' AND a.ActionId='2' AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId)AS AllGroupDirectorDakCount,
    (SELECT COUNT(DISTINCT a.DakId) AS AllGroupClosedDakCount FROM dak a LEFT JOIN dak_marking mark ON a.DakId = mark.DakId LEFT JOIN dak_member_type c ON mark.DakMemberTypeId = c.DakMemberTypeId  WHERE a.dakstatus IN ('DC')  AND a.ActionId='2' AND  a.ReceiptDate BETWEEN InFromDate AND InToDate AND c.MemberTypeGrouping = 'Y' AND mark.DakMemberTypeId = InDakMemberTypeId)AS AllGroupClosedDakCount,
    a.DakMemberTypeId AS 'MemberTypeId',
    a.DakMemberType AS 'MemberType' 
    FROM dak_member_type a 
    JOIN dak_marking b ON a.DakMemberTypeId=b.DakMemberTypeId
    WHERE a.MemberTypeGrouping='Y' AND b.DakMemberTypeId=InDakMemberTypeId;
    
END */$$
DELIMITER ;

