   END AS 'Status',
   (SELECT r.DakReplyId FROM dak_reply r WHERE b.DakId = r.DakId AND b.EmpId = r.EmpId AND r.ReplyStatus = 'R') AS 'DakReplyId',
   b.MsgType,b.IsActive,a.DakId,a.DakStatus,b.MarkerAction,
