DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakPendingDistributionList`(IN InDivisionCode varchar(50), IN InLabCode varchar(50), IN InUserName VARCHAR(50))
BEGIN
SELECT a.dakid, b.lettertype, c.priorityname, d.sourceshortname, a.Refno, a.dakstatus, a.Refdate, e.statusdesc, a.dakno, f.ActionRequired,
 IFNULL(dakmarking_counts.CountOfDakMarking, 0) AS 'dakMarking_count', 
 CASE WHEN IFNULL(dakmarking_counts.CountOfDakMarking, 0) = 0 THEN 'N' ELSE 'Y' END AS 'dakmarking_status',
     a.ActionId,a.ActionDueDate,a.Subject,
 CASE WHEN a.ProjectType = 'P' THEN p.Projectcode
     WHEN a.ProjectType = 'N' THEN n.NonShortName
     WHEN a.ProjectType = 'O' THEN o.ProjectCode
     ELSE 'NA' END AS 'Projectcode',a.PriorityId
     
      FROM dak a
      INNER JOIN dak_letter_type b ON a.lettertypeid = b.lettertypeid
      INNER JOIN dak_priority c ON a.priorityid = c.priorityid
      INNER JOIN dak_source_details d ON a.SourceDetailId = d.SourceDetailId
      INNER JOIN dak_status e ON a.dakstatus = e.dakstatus
      INNER JOIN dak_action f ON a.ActionId = f.ActionId
      LEFT  JOIN project_master p     ON p.ProjectId = a.ProjectId AND a.ProjectType = 'P'
      LEFT  JOIN dak_non_project n    ON n.NonProjectId = a.ProjectId AND a.ProjectType = 'N'
      LEFT  JOIN dak_others_project o ON o.ProjectOtherId = a.ProjectId AND a.ProjectType = 'O'
      LEFT JOIN (
        SELECT dakid, COUNT(DISTINCT DakMarkingId) AS CountOfDakMarking
        FROM dak_marking
        WHERE IsActive = '1'
        GROUP BY dakid
     ) AS dakmarking_counts ON a.dakid = dakmarking_counts.dakid



WHERE a.dakstatus='DI' AND a.LabCode=InLabCode

AND (
         (SELECT logintypeDms FROM login WHERE username = InUserName) IN ('A','Z','E','V') OR a.DivisionCode = InDivisionCode
    ) ORDER BY Dakid DESC;
 
END */$$
