/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakPendingList`(InFromDate VARCHAR(20), InToDate VARCHAR(20),InEmpId BIGINT(20),InUsername VARCHAR(20))
BEGIN
SELECT  a.dakid,b.lettertype,c.priorityname,d.sourceshortname,a.Refno,a.dakstatus,a.Refdate,e.statusdesc,a.dakno,
g.ActionRequired,a.ActionDueDate,a.sourceid,ClosedDate,
CASE WHEN  a.DakStatus = 'DC' AND a.ActionDueDate <= a.ClosedDate  THEN 'CO'
      WHEN a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate THEN 'CD'
      WHEN a.DakStatus <> 'DC' AND CURDATE() <= a.ActionDueDate   THEN 'OG'
      WHEN a.DakStatus <> 'DC' AND CURDATE() > a.ActionDueDate    THEN 'DO' 
      ELSE 'NA' END AS statusFlag,
     a.subject,a.ProjectType, a.ProjectId,f.DakAckStatus,f.DakMarkingId,
     f.DakAssignStatus,
     CASE WHEN a.ProjectType = 'P' THEN (SELECT p.Projectcode FROM project_master p WHERE a.ProjectId = p.ProjectId)
          WHEN a.ProjectType = 'N' THEN (SELECT n.NonShortName FROM dak_non_project n WHERE a.ProjectId = n.NonProjectId)
          WHEN a.ProjectType = 'O' THEN (SELECT o.ProjectCode FROM dak_others_project o WHERE a.ProjectId = o.ProjectOtherId)
          ELSE 'NA'
    END AS 'Projectcode',f.MsgType,
