(SELECT COUNT(*) FROM dak WHERE ProjectType='P' AND ActionId='2' AND ReceiptDate BETWEEN InFromDate AND InToDate) AS 'AllProjectActionCount',
(SELECT COUNT(*) FROM dak WHERE ProjectType='P' AND ActionId='1' AND ReceiptDate BETWEEN InFromDate AND InToDate) AS 'AllProjectRecordsCount',
(SELECT COUNT(*) FROM dak WHERE ProjectType='P' AND ActionId='2' AND DakStatus IN ('DD','DA') AND NOT EXISTS (SELECT 1 FROM dak_reply WHERE dak_reply.dakId = dakId) AND ReceiptDate BETWEEN InFromDate AND InToDate) AS 'AllDakProjectPendingReply',
