INNER JOIN dak_priority c ON a.priorityid = c.priorityid
INNER JOIN dak_source_details d ON a.SourceDetailId = d.SourceDetailId
INNER JOIN dak_status e ON a.dakstatus = e.dakstatus
INNER JOIN dak_action g ON g.ActionId = a.ActionId

LEFT JOIN ( SELECT DakId, COUNT(FileName) AS AttachmentCount  FROM dak_attachment  WHERE DakId = InDakId GROUP BY DakId ) att_count ON a.DakId = att_count.DakId
LEFT JOIN ( SELECT DakId, COUNT(DakLinkId) AS DakLinkCount  FROM dak_daklink WHERE DakId = InDakId GROUP BY DakId  ) link_count ON a.DakId = link_count.DakId
