    JOIN dak_marking m ON r.dakid = m.dakid AND r.EmpId = m.EmpId WHERE IsActive = '1' GROUP BY r.dakid  ) AS reply_counts ON a.dakid = reply_counts.dakid
WHERE a.DakId = InDakId;
END */$$
