/* Procedure structure for procedure `Dms_DakRemarknRedistributeList` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dms_DakRemarknRedistributeList` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakRemarknRedistributeList`(InFromDate VARCHAR(20), InToDate VARCHAR(20),InStatusValue VARCHAR(20))
BEGIN
SELECT  a.dakid, b.lettertype, c.priorityname, d.sourceshortname, a.Refno, a.dakstatus, a.Refdate, e.statusdesc, a.dakno,
 g.ActionRequired, a.ActionDueDate, a.sourceid,
 CASE 
     WHEN a.DakStatus = 'DC' AND a.ActionDueDate <= a.ClosedDate   THEN 'CO'
     WHEN a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate   THEN 'CD' 
     WHEN a.DakStatus <> 'DC' AND CURDATE() <= a.ActionDueDate    THEN 'OG'
     WHEN a.DakStatus <> 'DC' AND CURDATE() > a.ActionDueDate     THEN 'DO'
     ELSE 'NA' 
  END AS statusFlag,
     a.subject, a.ProjectType, a.ProjectId,a.DistributedDate,
    CASE 
        WHEN a.ProjectType = 'P' THEN p.Projectcode
        WHEN a.ProjectType = 'N' THEN n.NonShortName
        WHEN a.ProjectType = 'O' THEN o.ProjectCode
        ELSE 'NA' END AS 'Projectcode',
     a.ActionId,a.priorityId,a.DeliveryTypeId,IFNULL(remarkcount, 0) AS remark_count                    
     FROM dak a
     JOIN dak_letter_type b ON a.lettertypeid = b.lettertypeid
     JOIN dak_priority c ON a.priorityid = c.priorityid
     JOIN dak_source_details d ON a.sourcedetailid = d.sourcedetailid
     JOIN dak_status e ON a.dakstatus = e.dakstatus
     JOIN dak_action g ON a.ActionId = g.ActionId
     LEFT JOIN project_master p     ON p.ProjectId = a.ProjectId AND a.ProjectType = 'P'
     LEFT JOIN dak_non_project n    ON n.NonProjectId = a.ProjectId AND a.ProjectType = 'N'
     LEFT JOIN dak_others_project o ON o.ProjectOtherId = a.ProjectId AND a.ProjectType = 'O'
     LEFT JOIN (SELECT m.DakId,
        COUNT(DISTINCT CASE WHEN m.IsActive = '1' AND m.Remarkup = 'Y' THEN m.DakMarkingId END) AS remarkcount
     FROM dak_marking m
     GROUP BY DakId
    ) AS remarking_counts ON a.dakid = remarking_counts.DakId
      WHERE (a.ReceiptDate BETWEEN InFromDate AND InToDate) AND  a.dakstatus IN ('DD','DA','DR') 
     AND (
              (InStatusValue = 'All')
            OR (InStatusValue = 'Ongoing' AND a.DakStatus <> 'DC' AND CURDATE() <= a.ActionDueDate)
            OR (InStatusValue = 'OngoingDelay' AND a.DakStatus <> 'DC' AND CURDATE() > a.ActionDueDate)
            OR (InStatusValue = 'Completed' AND a.DakStatus = 'DC' AND a.ActionDueDate = a.ClosedDate)
            OR (InStatusValue = 'CompletedDelay' AND a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate)
        )

  ORDER BY dakid DESC;
  END */$$
DELIMITER ;

