DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakRepliedByMeList`(InFromDate VARCHAR(20), InToDate VARCHAR(20),InEmpId VARCHAR(20),InLetterTypeId VARCHAR(20),InPriorityid VARCHAR(20),InSourcedetailid VARCHAR(20),InSourceId VARCHAR(20),InProjectType VARCHAR(20),InProjectId VARCHAR(20),InMemberTyPeId VARCHAR(20),InEmployeeId VARCHAR(20))
BEGIN
SELECT a.dakid, b.lettertype, c.priorityname, d.sourceshortname, a.Refno, a.dakstatus, a.Refdate, e.statusdesc, a.dakno,
    g.ActionRequired, a.ActionDueDate, a.sourceid, a.subject, a.ProjectType, a.ProjectId, f.Remarks,
    CASE
        WHEN a.ProjectType = 'P' THEN (SELECT p.Projectcode FROM project_master p WHERE a.ProjectId = p.ProjectId)
        WHEN a.ProjectType = 'N' THEN (SELECT n.NonShortName FROM dak_non_project n WHERE a.ProjectId = n.NonProjectId)
        WHEN a.ProjectType = 'O' THEN (SELECT o.ProjectCode FROM dak_others_project o WHERE a.ProjectId = o.ProjectOtherId)
        ELSE 'NA'
    END AS 'Projectcode',
    i.EmpName, j.Designation, h.DakMarkingId, h.DakAssignStatus, h.DakAckStatus,
     IFNULL(CountOfAllMarkers, 0) AS CountOfAllMarkers,
     IFNULL(CountOfActionMarkers, 0) AS CountOfActionMarkers,
     IFNULL(CountOfMarkersAck, 0) AS CountOfMarkersAck,
     IFNULL(CountOfActMarkersReply, 0) AS CountOfActMarkersReply 
FROM dak a
JOIN  dak_letter_type b ON a.lettertypeid = b.lettertypeid
JOIN  dak_priority c ON a.priorityid = c.priorityid
JOIN dak_source_details d ON a.sourcedetailid = d.sourcedetailid
JOIN  dak_status e ON a.dakstatus = e.dakstatus
JOIN dak_action g ON a.ActionId = g.ActionId
JOIN dak_reply f ON a.DakId = f.DakId
JOIN  employee i ON f.EmpId = i.EmpId
JOIN employee_desig j ON i.DesigId = j.DesigId
JOIN dak_source s ON a.SourceId = s.SourceId
JOIN dak_delivery_type t ON a.DeliveryTypeId=t.DeliveryTypeId
JOIN dak_marking h ON a.DakId = h.DakId AND f.EmpId = h.EmpId

LEFT JOIN (SELECT m.DakId,
  COUNT(DISTINCT CASE WHEN m.IsActive = '1' THEN m.DakMarkingId END) AS CountOfAllMarkers,
  COUNT(DISTINCT CASE WHEN m.IsActive = '1' /*AND m.MarkerAction = 'A'*/ THEN m.DakMarkingId END) AS CountOfActionMarkers,
  COUNT(DISTINCT CASE WHEN m.IsActive = '1' AND m.DakAckStatus = 'Y' THEN m.DakMarkingId END) AS CountOfMarkersAck
 FROM dak_marking m
     GROUP BY DakId
     ) AS marking_counts ON a.dakid = marking_counts.DakId

    LEFT JOIN (
    SELECT r.dakid,
        COUNT(DISTINCT DakReplyId) AS CountOfActMarkersReply
    FROM dak_reply r
    JOIN dak_marking m ON r.dakid = m.dakid AND r.EmpId = m.EmpId
    WHERE IsActive = '1' /*AND MarkerAction = 'A'*/
    GROUP BY dakid
    ) AS reply_counts ON a.dakid = reply_counts.dakid

WHERE  a.dakstatus IN ('DD','DR','RP','RM','FP') AND (a.ReceiptDate BETWEEN InFromDate AND InToDate) AND f.EmpId = InEmpId
 AND (
        (InLetterTypeId = 'All') OR (InLetterTypeId = a.DeliveryTypeId)
    )
    AND (
        (InPriorityid = 'All') OR (InPriorityid = a.priorityid)
    )
    AND (
	(InSourcedetailid = 'All') OR (InSourcedetailid = a.sourcedetailid)
    )
    AND (
        (InSourceId = 'All') OR (InSourceId = a.SourceId)
    )
    AND (
        (InProjectType = 'All') OR (InProjectType = a.ProjectType)
    )
    AND (
        (InProjectId = 'All') OR (InProjectId = a.ProjectId)
    )
    AND (
        (InMemberTyPeId = 'All') OR (InMemberTyPeId = h.DakMemberTypeId)
    )
    AND (
        (InEmployeeId = 'All') OR (InEmployeeId = h.EmpId)
    )

ORDER BY a.dakid DESC;


END */$$
DELIMITER ;

