/* Procedure structure for procedure `Dms_DakSLAMissedDashBoardCount` */

/*!50003 DROP PROCEDURE IF EXISTS  `Dms_DakSLAMissedDashBoardCount` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Dms_DakSLAMissedDashBoardCount`(InFromDate VARCHAR(20), InToDate VARCHAR(20),InTypeValue  VARCHAR(20),InProjectType  VARCHAR(20),InSourceTypeId  VARCHAR(20),InProjectTypeId  VARCHAR(20),InMemberTypeId  VARCHAR(20),InEmployeeId VARCHAR(20),InDeliveryTypeId  VARCHAR(20),InPriorityId VARCHAR(20))
BEGIN
SELECT  a.dakid, b.lettertype, c.priorityname, DATEDIFF(a.ActionDueDate, DATE(SYSDATE())), d.sourceshortname, a.Refno, a.dakstatus, a.Refdate, e.statusdesc, a.dakno, g.ActionRequired, a.ActionDueDate, a.sourceid, a.ClosedDate,
 a.subject, a.ProjectType, a.ProjectId, a.DistributedDate,
 CASE 
     WHEN a.DakStatus = 'DC' AND a.ActionDueDate <= a.ClosedDate   THEN 'CO'
     WHEN a.DakStatus = 'DC' AND a.ClosedDate > a.ActionDueDate   THEN 'CD' 
     WHEN a.DakStatus <> 'DC' AND CURDATE() <= a.ActionDueDate    THEN 'OG'
     WHEN a.DakStatus <> 'DC' AND CURDATE() > a.ActionDueDate     THEN 'DO'
     ELSE 'NA' 
  END AS statusFlag,
    CASE 
        WHEN a.ProjectType = 'P' THEN p.Projectcode
        WHEN a.ProjectType = 'N' THEN n.NonShortName
        WHEN a.ProjectType = 'O' THEN o.ProjectCode
    ELSE 'NA' END AS 'Projectcode', a.ActionId, a.ClosedBy, a.priorityId, a.Signatory, a.DeliveryTypeId, a.ApprovedBy, a.DirectorApproval, a.ClosingAuthority,
    CASE
      WHEN EXISTS (SELECT 1 FROM dak_reply r, dak_marking b WHERE b.DakId = r.DakId AND b.EmpId = r.EmpId AND r.ReplyStatus = 'R' AND b.DakId = a.DakId) THEN 'Replied'
      ELSE 'Not Replied'
    END AS 'ReplyStatus'                        
      FROM dak a
      JOIN dak_letter_type b ON a.lettertypeid = b.lettertypeid
      JOIN dak_priority c ON a.priorityid = c.priorityid
      JOIN dak_source_details d ON a.sourcedetailid = d.sourcedetailid
      JOIN dak_status e ON a.dakstatus = e.dakstatus
      JOIN dak_action g ON a.ActionId = g.ActionId
     LEFT  JOIN project_master p     ON p.ProjectId = a.ProjectId AND a.ProjectType = 'P'
     LEFT  JOIN dak_non_project n    ON n.NonProjectId = a.ProjectId AND a.ProjectType = 'N'
     LEFT JOIN dak_others_project o ON o.ProjectOtherId = a.ProjectId AND a.ProjectType = 'O'
     WHERE (a.ReceiptDate BETWEEN InFromDate AND InToDate) AND DATEDIFF(a.ActionDueDate, DATE(SYSDATE())) <= 0
     AND ((InTypeValue = 'All')OR (a.SourceId = InTypeValue)) AND ((InProjectType = 'All')OR (a.ProjectType=InProjectType))
     AND ((InSourceTypeId = 'All')OR (a.SourceDetailId=InSourceTypeId)) AND ((InProjectTypeId = 'All')OR (a.ProjectId=InProjectTypeId))
     and ((InDeliveryTypeId = 'All')OR (a.DeliveryTypeId = InDeliveryTypeId)) AND ((InPriorityId = 'All')OR (a.PriorityId=InPriorityId)) 
     AND  ((InMemberTypeId = 'All')OR ( InMemberTypeId IN (SELECT m.DakMemberTypeId FROM dak_marking m WHERE m.DakId=a.DakId) ) )AND 
     ((InEmployeeId= 'All')OR ( InEmployeeId IN (SELECT m.EmpId FROM dak_marking m WHERE m.DakId=a.DakId) ))
  ORDER BY dakid DESC;
   
END */$$
DELIMITER ;

