/* Procedure structure for procedure `Employee_Dropdown` */

/*!50003 DROP PROCEDURE IF EXISTS  `Employee_Dropdown` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Employee_Dropdown`( IN InEmpId BIGINT(20),IN LogInType VARCHAR(25),IN InProjectId BIGINT(20))
BEGIN
SELECT e.empid,CONCAT(IFNULL(CONCAT(e.title,' '),''), e.empname) as 'empname',ed.designation, ed.desigcode,e.srno,e.divisionid
FROM employee e, employee_desig ed
WHERE e.desigid=ed.desigid AND e.isactive=1 AND
CASE 	
	WHEN logintype IN ('A','H','Z','C','I','Y')
		THEN 
			CASE WHEN InProjectId>0
			THEN e.empid IN ( SELECT pe.empid 
						FROM project_employee pe 
						WHERE   pe.projectid=InProjectId 
					)	
			 WHEN InProjectId=0
			 THEN 1=1
			 END
			 
	WHEN logintype='D'
		THEN e.empid IN ( SELECT e.empid FROM division_master dm, employee e WHERE  dm.divisionid=e.divisionid AND dm.divisionheadid=InEmpId
					UNION
					SELECT dm1.divisionheadid FROM division_master dm1 WHERE dm1.isactive=1 AND dm1.divisionheadid <> InEmpId
					/*AND InProjectId>0*/ 
				)
				
	WHEN logintype='G'
		THEN e.empid IN (SELECT e.empid	FROM division_group dg, division_master dm, employee e WHERE dm.groupid=dg.groupid 
					AND dm.divisionid=e.divisionid AND dg.groupheadid=InEmpId 
				UNION
					SELECT dg1.groupheadid FROM division_group dg1 WHERE dg1.groupheadid <> InEmpId
				)
				
	WHEN logintype='T'
		THEN  e.empid IN(  SELECT e.empid FROM division_group dg, division_master dm, employee e WHERE dm.groupid=dg.groupid 
							AND dm.divisionid=e.divisionid AND dg.groupheadid=InEmpId 
					
					UNION
					SELECT dg1.groupheadid FROM division_group dg1 WHERE dg1.groupheadid <> InEmpId  
					
					UNION
					SELECT dm1.divisionheadid FROM division_master dm1 WHERE dm1.isactive=1 AND dm1.divisionheadid <> InEmpId
					
				)
	
					
	WHEN logintype IN ('P','C')
		THEN e.empid IN ( SELECT pe.empid 
					FROM project_employee pe 
					WHERE pe.projectid = InProjectId
				
					UNION 
					SELECT dm1.divisionheadid FROM division_master dm1 WHERE dm1.isactive=1 AND dm1.divisionheadid <> InEmpId 
					AND InProjectId>0 
					
					UNION
					SELECT empid FROM employee WHERE empid=InEmpId AND InProjectId=0
				)					
					
					
	WHEN logintype='E'
		THEN
			CASE WHEN InProjectId>0
				THEN e.empid IN (SELECT dm1.divisionheadid FROM division_master dm1 WHERE dm1.isactive=1 AND dm1.divisionheadid <> InEmpId
					AND InProjectId>0 
				UNION
				SELECT pe.empid FROM project_employee pe WHERE pe.projectid AND pe.projectid=InProjectId  )	
			 WHEN InProjectId=0
				THEN 1=1
			 END	
	
		
	WHEN logintype='F'
		THEN  e.empid IN(
				SELECT e.empid FROM division_master dm, employee e WHERE  dm.divisionid=e.divisionid AND dm.divisionheadid=InEmpId 
				UNION
				SELECT dm1.divisionheadid FROM division_master dm1 WHERE dm1.isactive=1 AND dm1.divisionheadid <> InEmpId 
