        d.subdescription AS subofsub, a.minutesid AS mainid,0 AS subid,a.minutessubofsubid AS subofsubid,f.projectid
	,(SELECT COUNT(de.actionmainid ) FROM action_main de WHERE a.scheduleminutesid=de.scheduleminutesid AND de.actiontype='S')
