/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfms_Action_Monitoring`(In InProjectId BIGINT(20),In InStatus varchar(1))
BEGIN
select * from (

SELECT aa.ActionAssignId,aa.ActionMainId,aa.ActionNo,am.actionitem,am.actiondate,aa.enddate,aa.PDCOrg,aa.PDC1,aa.PDC2,aa.Revision,aa.actionstatus,aa.actionflag,
 (SELECT  progress FROM action_sub  WHERE  actionsubid=(SELECT  MAX(actionsubid) FROM action_sub  WHERE actionassignid=aa.actionassignid) AND   actionassignid=aa.actionassignid) AS 'progess',
CONCAT(IFNULL(CONCAT(e1.title,' '),''), e1.empname) AS 'assignor', ed1.desigcode AS 'assignordesig',CONCAT(IFNULL(CONCAT(e2.title,' '),''), e2.empname) AS 'assignee', ed2.desigcode AS 'assigneedesig',
(SELECT  progressdate FROM action_sub  WHERE  actionsubid=(SELECT  MAX(actionsubid) FROM action_sub  WHERE actionassignid=aa.actionassignid) AND   actionassignid=aa.actionassignid) AS 'progressdate'
FROM action_assign aa, action_main am, employee e1, employee_desig ed1, employee e2, employee_desig ed2
WHERE am.ActionMainId=aa.ActionMainId AND aa.Assignor=e1.empid AND e1.desigid=ed1.desigid
AND aa.Assignee=e2.empid AND e2.desigid=ed2.desigid AND aa.isactive=1 AND am.projectid= InProjectId AND aa.assigneelabcode <> '@EXP'

UNION

SELECT aa.ActionAssignId,aa.ActionMainId,aa.ActionNo,am.actionitem,am.actiondate,aa.enddate,aa.PDCOrg,aa.PDC1,aa.PDC2,aa.Revision,aa.actionstatus,aa.actionflag,
 (SELECT  progress FROM action_sub  WHERE  actionsubid=(SELECT  MAX(actionsubid) FROM action_sub  WHERE actionassignid=aa.actionassignid) AND   actionassignid=aa.actionassignid) AS 'progess',
CONCAT(IFNULL(CONCAT(e1.title,' '),''), e1.empname) AS 'assignor', ed1.desigcode AS 'assignordesig',CONCAT(IFNULL(CONCAT(e2.title,' '),''), e2.expertname) AS 'empname', 'EXPERT' AS 'assigneedesig',
(SELECT  progressdate FROM action_sub  WHERE  actionsubid=(SELECT  MAX(actionsubid) FROM action_sub  WHERE actionassignid=aa.actionassignid) AND   actionassignid=aa.actionassignid) AS 'progressdate'
FROM action_assign aa, action_main am, employee e1, employee_desig ed1, expert e2
WHERE am.ActionMainId=aa.ActionMainId AND aa.Assignor=e1.empid AND e1.desigid=ed1.desigid
AND aa.Assignee=e2.expertid AND aa.isactive=1 AND am.projectid= InProjectId AND aa.assigneelabcode = '@EXP'



) as m where
case 	when InStatus = 'A' then (m.progess is null or m.progess=0) AND  m.actionstatus <> 'C' 
	when InStatus = 'P' then m.progess>0 and m.progess<100 and  m.actionstatus <> 'C' 
	when InStatus = 'C' then m.actionstatus = 'C' 
end 
order by enddate asc
;
END */$$
