/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfms_Action_PDC_Report`(inEmpId VARCHAR(20),inProjectId VARCHAR(20),inPosition VARCHAR(1),inFromDate DATE,inToDate DATE)
BEGIN
      SELECT aas.actionno,CONCAT(IFNULL(CONCAT(b.title,' '),''), b.empname) AS 'assignoremp',c.designation AS desig,CONCAT(IFNULL(CONCAT(ab.title,' '),''), ab.empname) AS 'assigneeempname' ,dc.designation,aas.actionflag,aas.enddate,a.actionitem,0 AS oby,
      a.actionmainid,a.actionlinkid,(SELECT d.progress FROM action_sub d  WHERE d.actionassignid = aas.actionassignid AND d.actionsubid = (SELECT MAX(f.actionsubid) FROM action_sub f WHERE f.actionassignid = aas.actionassignid) )  AS progress , aas.actionassignid
      FROM action_main a,  employee b ,employee_desig c,  employee ab ,employee_desig dc , action_assign aas
      WHERE  (CASE WHEN 'A'=inEmpId THEN 1=1 ELSE aas.assignee=inEmpId END ) AND  aas.assignee=ab.empid AND a.actionmainid=aas.actionmainid  AND ab.isactive='1' AND dc.desigid=ab.desigid AND a.projectid=inProjectId
      AND  aas.assignor=b.empid  AND b.isactive='1' AND c.desigid=b.desigid AND (CASE WHEN 'P'=inPosition THEN aas.enddate BETWEEN inFromDate AND inToDate  
      WHEN 'S'=inPosition THEN a.actiondate BETWEEN inFromDate AND inToDate 
      WHEN 'A'=inPosition THEN 1=1 END ) AND aas.AssigneeLabCode <> '@EXP' 
      UNION
      SELECT aas.actionno,CONCAT(IFNULL(CONCAT(b.title,' '),''), b.empname) AS 'assignoremp',c.designation AS desig,CONCAT(IFNULL(CONCAT(ab.title,' '),''), ab.empname) AS 'assigneeempname' ,dc.designation,aas.actionflag,aas.enddate,a.actionitem,1 AS oby,
       a.actionmainid,a.actionlinkid,(SELECT d.progress FROM action_sub d  WHERE d.actionassignid = aas.actionassignid AND d.actionsubid = (SELECT MAX(f.actionsubid) FROM action_sub f WHERE f.actionassignid = aas.actionassignid) )  AS progress , aas.actionassignid
      FROM action_main a,  employee b ,employee_desig c,  employee ab ,employee_desig dc , action_assign aas
      WHERE (CASE WHEN 'A'=inEmpId THEN 1=0 ELSE aas.assignor=inEmpId END ) AND  aas.assignee=ab.empid AND a.actionmainid=aas.actionmainid AND ab.isactive='1' AND dc.desigid=ab.desigid AND a.projectid=inProjectId
      AND  aas.assignor=b.empid  AND b.isactive='1' AND c.desigid=b.desigid AND (CASE WHEN 'P'=inPosition THEN aas.enddate BETWEEN inFromDate AND inToDate  
      WHEN 'S'=inPosition THEN a.actiondate BETWEEN inFromDate AND inToDate 
      WHEN 'A'=inPosition THEN 1=1 END ) AND aas.AssigneeLabCode <> '@EXP' 
 
 union
 
	SELECT aas.actionno,CONCAT(IFNULL(CONCAT(b.title,' '),''), b.empname) AS 'assignoremp',c.designation AS desig,CONCAT(IFNULL(CONCAT(ab.title,' '),''), ab.expertname) AS 'assigneeempname' ,'Expert' as 'assigneedesig',aas.actionflag,aas.enddate,a.actionitem,0 AS oby,
       a.actionmainid,a.actionlinkid,(SELECT d.progress FROM action_sub d  WHERE d.actionassignid = aas.actionassignid AND d.actionsubid = (SELECT MAX(f.actionsubid) FROM action_sub f WHERE f.actionassignid = aas.actionassignid) )  AS progress , aas.actionassignid
      FROM action_main a,  employee b ,employee_desig c,  expert ab , action_assign aas
      WHERE  (CASE WHEN 'A'=inEmpId THEN 1=1 ELSE aas.assignee=inEmpId END ) AND  aas.assignee=ab.expertid AND a.actionmainid=aas.actionmainid  AND ab.isactive='1' AND a.projectid=inProjectId
      AND  aas.assignor=b.empid  AND b.isactive='1' AND c.desigid=b.desigid AND (CASE WHEN 'P'=inPosition THEN aas.enddate BETWEEN inFromDate AND inToDate  
      WHEN 'S'=inPosition THEN a.actiondate BETWEEN inFromDate AND inToDate 
      WHEN 'A'=inPosition THEN 1=1 END ) AND aas.AssigneeLabCode = '@EXP' 
      UNION
      SELECT aas.actionno,CONCAT(IFNULL(CONCAT(b.title,' '),''), b.empname) AS 'assignoremp',c.designation AS desig,CONCAT(IFNULL(CONCAT(ab.title,' '),''), ab.expertname) AS 'assigneeempname' ,'Expert' AS 'assigneedesig',aas.actionflag,aas.enddate,a.actionitem,1 AS oby,
       a.actionmainid,a.actionlinkid,(SELECT d.progress FROM action_sub d  WHERE d.actionassignid = aas.actionassignid AND d.actionsubid = (SELECT MAX(f.actionsubid) FROM action_sub f WHERE f.actionassignid = aas.actionassignid) )  AS progress , aas.actionassignid
      FROM action_main a,  employee b ,employee_desig c,  expert ab , action_assign aas
      WHERE (CASE WHEN 'A'=inEmpId THEN 1=0 ELSE aas.assignor=inEmpId END ) AND  aas.assignee=ab.expertid AND a.actionmainid=aas.actionmainid AND ab.isactive='1' AND a.projectid=inProjectId
      AND  aas.assignor=b.empid  AND b.isactive='1' AND c.desigid=b.desigid AND (CASE WHEN 'P'=inPosition THEN aas.enddate BETWEEN inFromDate AND inToDate  
      WHEN 'S'=inPosition THEN a.actiondate BETWEEN inFromDate AND inToDate 
      WHEN 'A'=inPosition THEN 1=1 END ) AND aas.AssigneeLabCode = '@EXP'       
      
      
      
       ORDER BY  oby ASC; 
      
END */$$
