DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `pfms_ccm_asp_status_list`(IN InScheduleId VARCHAR(20))
BEGIN
		SELECT a.InitiationMilestoneId, a.InitiationId, b.LabCode, b.ProjectShortName, b.ProjectTitle, e.ProjectTypeShort AS 'Category', b.ProjectCost, b.ProjectDuration,
			CONCAT(IFNULL(CONCAT(f.title,' '),(IFNULL(CONCAT(f.Salutation, ' '), ''))), f.empname,', ', g.Designation) AS 'PDD',
			a.PDRProbableDate, MAX(c.PDRProbableDate) AS 'PDRProbableDateRev', a.PDRActualDate, 
		        a.TIECProbableDate, MAX(c.TIECProbableDate) AS 'TIECProbableDateRev', a.TIECActualDate, 
		        a.CECProbableDate, MAX(c.CECProbableDate) AS 'CECProbableDateRev', a.CECActualDate, 
		        a.CCMProbableDate, MAX(c.CCMProbableDate) AS 'CCMProbableDateRev', a.CCMActualDate,
		        a.DMCProbableDate, MAX(c.DMCProbableDate) AS 'DMCProbableDateRev', a.DMCActualDate, 
		        a.SanctionProbableDate, MAX(c.SanctionProbableDate) AS 'SanctionProbableDateRev', a.SanctionActualDate,
		        d.MilestoneStatus, IFNULL(d.CCMASPStatusId, 0) AS 'CCMASPStatusId'
		FROM pfms_initiation_ms a
		JOIN pfms_initiation b ON a.InitiationId=b.InitiationId AND a.IsActive=1 AND a.IsActive=1
		LEFT JOIN pfms_initiation_ms_rev c ON a.InitiationMilestoneId=c.InitiationMilestoneId
		LEFT JOIN project_type e ON e.ProjectTypeId = b.ProjectTypeId
		LEFT JOIN cluster_lab_employee f ON f.EmpId=b.EmpId AND f.LabCode=b.LabCode
		LEFT JOIN employee_desig g ON f.DesigId=g.DesigId
		LEFT JOIN pfms_ccm_asp_status d ON d.InitiationId=a.InitiationId AND d.IsActive=1 AND d.ScheduleId=InScheduleId
		GROUP BY a.InitiationMilestoneId, a.InitiationId, b.LabCode, b.ProjectShortName, b.ProjectTitle, e.ProjectTypeShort, 
		b.ProjectCost, b.ProjectDuration, f.title, f.Salutation, f.empname, g.Designation, a.PDRProbableDate, a.PDRActualDate,
		a.TIECProbableDate, a.TIECActualDate, a.CECProbableDate, a.CECActualDate, a.CCMProbableDate, a.CCMActualDate, 
		a.DMCProbableDate, a.DMCActualDate, a.SanctionProbableDate, a.SanctionActualDate, d.MilestoneStatus, d.CCMASPStatusId
		ORDER BY b.LabCode,b.InitiationId
		;
	END */$$
