/* Procedure structure for procedure `pfms_ccm_calendar_data` */

/*!50003 DROP PROCEDURE IF EXISTS  `pfms_ccm_calendar_data` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `pfms_ccm_calendar_data`(IN InDate VARCHAR(50), IN InMeetingType VARCHAR(50), IN InDays INT(20), IN InClusterId VARCHAR(20))
BEGIN
		
		
		SELECT a.LabCode,

		/* Previous Month Proposed */
		(SELECT GROUP_CONCAT(CONCAT(c.ProjectShortName, '/', 
			(CASE 
				WHEN DATE_ADD(c.SanctionDate, INTERVAL FLOOR(DATEDIFF(InDate, c.SanctionDate) / InDays) * InDays DAY) IN 
					(SELECT h.ScheduleDate FROM cluster_committee_schedule h 
					WHERE SUBSTRING_INDEX(SUBSTRING_INDEX(h.MeetingId, '/', 2), '/', -1)=c.ProjectCode AND h.LabCode=c.LabCode AND h.IsActive=1 
					AND h.CommitteeId = (SELECT i.CommitteeId FROM committee i WHERE i.IsActive=1 AND i.CommitteeShortName=InMeetingType) )
				THEN 'black' 
				ELSE 'red' 
			END)
			) ORDER BY c.ProjectShortName SEPARATOR ', ')
		FROM project_master c
		WHERE c.IsActive = 1 AND c.LabCode = a.LabCode AND c.IsMainWC<>0
		AND DATE_ADD(c.SanctionDate, INTERVAL FLOOR(DATEDIFF(InDate, c.SanctionDate) / InDays) * InDays DAY) BETWEEN DATE_FORMAT(InDate - INTERVAL 1 MONTH, '%Y-%m-01') AND LAST_DAY(InDate - INTERVAL 1 MONTH)
		) AS PreviousMonthProposed,


		/* Previous Month Held */
		(SELECT GROUP_CONCAT(CONCAT(d.ProjectShortName, '/', 
			(CASE 
				WHEN DATE_FORMAT(DATE_ADD(d.SanctionDate, INTERVAL FLOOR(DATEDIFF(InDate, d.SanctionDate) / InDays) * InDays DAY), '%Y-%m') = DATE_FORMAT(g.ScheduleDate, '%Y-%m')
				THEN '#f502f5' 
				ELSE 'blue' 
			END)
			) SEPARATOR ', ')
		FROM project_master d
		JOIN cluster_committee_schedule g ON g.IsActive = 1 AND SUBSTRING_INDEX(SUBSTRING_INDEX(g.MeetingId, '/', 2), '/', -1)=d.ProjectCode AND g.LabCode=d.LabCode AND d.IsMainWC<>0 
			AND g.CommitteeId IN (SELECT c.CommitteeId FROM committee c WHERE c.LabCode = d.LabCode AND c.CommitteeShortName = InMeetingType)
		JOIN committee_meeting_status f ON g.ScheduleFlag = f.MeetingStatus AND f.MeetingStatusId > 5
		WHERE d.LabCode = a.LabCode AND g.ScheduleDate BETWEEN DATE_FORMAT(InDate - INTERVAL 1 MONTH, '%Y-%m-01') AND LAST_DAY(InDate - INTERVAL 1 MONTH)
		) AS PreviousMonthHeld,

		/* Current Month Proposed */
		(SELECT GROUP_CONCAT(e.ProjectShortName  ORDER BY e.ProjectShortName SEPARATOR ', ')
		FROM project_master e
		WHERE e.IsActive = 1 AND e.LabCode = a.LabCode AND e.IsMainWC<>0
		AND DATE_ADD(e.SanctionDate, INTERVAL FLOOR(DATEDIFF(InDate, e.SanctionDate) / InDays) * InDays DAY) BETWEEN DATE_FORMAT(InDate, '%Y-%m-01') AND LAST_DAY(InDate)
		) AS CurrentMonthProposed

		FROM cluster_lab a, lab_master b 
