DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfms_Com_Con_Approval`( InLoginType VARCHAR(5),InEmpId VARCHAR(5))
BEGIN
SELECT DISTINCT  cm.committeemainid,c.committeeshortname,c.committeename,cca.constitutionstatus,
CASE 
	WHEN cm.projectid>0 THEN (SELECT pm.projectcode FROM project_master pm WHERE cm.ProjectId=pm.ProjectId)
	WHEN cm.divisionid>0 THEN (SELECT dm.divisioncode FROM division_master dm WHERE cm.divisionid=dm.divisionid)  
	WHEN cm.initiationid>0 THEN (SELECT im.projectshortname FROM pfms_initiation im WHERE cm.initiationid=im.initiationid)
	ELSE 'Non-Project'
END AS 'comtype'
FROM committee_main cm, committee c, committee_constitution_approval cca
WHERE cm.status='P' AND cm.committeeid=c.committeeid AND cm.committeemainid=cca.committeemainid 
AND 
CASE 
	WHEN  InLoginType IN ('T','J')   THEN cca.constitutionstatus IN ('CFW','RTR') 
	AND cca.empid IN ( SELECT l.empid FROM employee l, division_group gm,division_master dm WHERE gm.groupid=dm.groupid AND dm.divisionid=l.divisionid AND gm.groupheadid=InEmpId ) 
	WHEN  InLoginType IN ('G','D','F')   THEN cca.constitutionstatus IN ('CFW','RTR') 
	AND cca.empid IN(SELECT l.empid FROM employee l, division_master dm WHERE  dm.divisionid=l.divisionid AND dm.divisionheadid=InEmpId)
	WHEN  InLoginType='E'   THEN cca.constitutionstatus IN ('RDO','RTDR') 
	WHEN  InLoginType in  ('Z','C','I')  THEN cca.constitutionstatus IN ('RDT','RTDG') 
	
	WHEN  InLoginType='A'    THEN  cca.constitutionstatus IN ('CFW','RTR','RDO','RTDR','RDT','RTDG','RDR','RTSC','RDG')
	
	ELSE  cca.constitutionstatus IN ('CCR')
 
END;
 END */$$
DELIMITER ;

