DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfms_Meeting_Reports`(IN InEmpId CHAR(7),IN InSchDate VARCHAR(1),IN InProId VARCHAR(3),IN InDivId VARCHAR(3), IN InIniId VARCHAR(3),IN InLoginType VARCHAR(2),IN InLabCode VARCHAR(20)  )
BEGIN 

SELECT b.scheduleid,b.scheduledate,b.schedulestarttime,b.scheduleflag,
c.committeeshortname,b.meetingid,b.meetingvenue,b.projectid,b.divisionid,b.initiationid

FROM committee_main a,committee_schedule b ,committee c,committee_member cm

WHERE b.isactive=1 AND b.labcode=InLabCode AND

CASE WHEN InLoginType NOT IN ('A','Z','Y','I','C')
THEN
cm.empid = InEmpId  AND cm.membertype IN ('CC','CS','CI') AND cm.labcode=InLabCode  AND cm.isactive=1
AND cm.committeemainid=a.committeemainid 
AND (b.committeemainid=a.committeemainid OR (a.committeeid=b.committeeid AND a.projectid=b.projectid AND a.divisionid=b.divisionid AND a.isactive=1))
ELSE
1=1
END


AND b.committeeid=c.committeeid AND
 (CASE 	WHEN 'T'=InSchDate THEN CURDATE()=b.scheduledate  
	WHEN 'S'=InSchDate THEN b.scheduledate BETWEEN CURDATE() AND DATE_ADD(CURDATE(), INTERVAL 7 DAY ) 
	WHEN 'E'=InSchDate THEN DATE_ADD(CURDATE(), INTERVAL 1 DAY )= b.scheduledate END) 
AND a.committeeid=c.committeeid AND
 CASE 	WHEN 'A'=InProId AND 'A'=InDivId AND 'A'=InIniId 
	THEN  1=1 
	ELSE
		(CASE WHEN 'A'=InProId THEN b.projectid>0  ELSE b.projectid=InProId END) 
		AND (CASE WHEN 'A'=InDivId THEN  b.divisionid>0 ELSE b.divisionid=InDivId END) 
		AND (CASE WHEN 'A'=InIniId THEN b.initiationid>0 ELSE b.initiationid=InIniId END) 
	END
UNION 


DISTINCT
SELECT b.scheduleid,b.scheduledate,b.schedulestarttime,b.scheduleflag,
d.committeeshortname,b.meetingid,b.meetingvenue,b.projectid,b.divisionid,b.initiationid
 
FROM committee_schedules_invitation a,committee_schedule b,committee_main c ,committee d
WHERE b.isactive=1 AND a.committeescheduleid=b.scheduleid AND b.committeemainid=c.committeemainid AND a.empid=InEmpId AND a.labcode=InLabCode AND b.labcode=InLabCode 
AND 	(CASE 	WHEN 'T'=InSchDate THEN CURDATE()=b.scheduledate  
		WHEN 'S'=InSchDate THEN b.scheduledate BETWEEN CURDATE() AND DATE_ADD(CURDATE(), INTERVAL 7 DAY ) 
		WHEN 'E'=InSchDate THEN DATE_ADD(CURDATE(), INTERVAL 1 DAY )= b.scheduledate 
	END) 
AND d.committeeid=c.committeeid AND
 CASE 	WHEN 'A'=InProId AND 'A'=InDivId AND 'A'=InIniId 
	THEN  1=1 
	ELSE
		(CASE WHEN 'A'=InProId THEN b.projectid>0  ELSE b.projectid=InProId END) 
		AND (CASE WHEN 'A'=InDivId THEN  b.divisionid>0 ELSE b.divisionid=InDivId END) 
		AND (CASE WHEN 'A'=InIniId THEN b.initiationid>0 ELSE b.initiationid=InIniId END) 
	END;
 END */$$
DELIMITER ;

