AND (CASE WHEN 'T'=InSchDate THEN CURDATE()=cs.scheduledate  WHEN 'S'=InSchDate THEN cs.scheduledate BETWEEN CURDATE() AND DATE_ADD(CURDATE(), INTERVAL 7 DAY ) WHEN 'E'=InSchDate THEN DATE_ADD(CURDATE(), INTERVAL 1 DAY )= cs.scheduledate END) 
