/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfms_Milestone_Quarter`(IN InProjId varchar(20),InQuater int(1),InYear int(4))
BEGIN 
SELECT 
       (CASE WHEN  InQuater=1 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.orgenddate) >= 1 AND MONTH(a.orgenddate) <= 3 and year(a.orgenddate)=InYear and a.projectid=InProjId) 
       
           WHEN  InQuater=2 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.orgenddate) >= 4 AND MONTH(a.orgenddate) <= 6  AND YEAR(a.orgenddate)=InYear  AND a.projectid=InProjId) 
       
           WHEN  InQuater=3 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.orgenddate) >= 7 AND MONTH(a.orgenddate) <= 9 AND YEAR(a.orgenddate)=InYear   AND a.projectid=InProjId) 
       
           WHEN  InQuater=4 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.orgenddate) >= 10 AND MONTH(a.orgenddate) <= 12 AND YEAR(a.orgenddate)=InYear   AND a.projectid=InProjId) ELSE 0 END) AS oldone,
           
      (CASE WHEN  InQuater=1 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.enddate) >= 1 AND MONTH(a.enddate) <= 3 AND YEAR(a.enddate)=InYear AND a.projectid=InProjId) 
       
           WHEN  InQuater=2 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.enddate) >= 4 AND MONTH(a.enddate) <= 6  AND YEAR(a.enddate)=InYear  AND a.projectid=InProjId) 
       
           WHEN  InQuater=3 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.enddate) >= 7 AND MONTH(a.enddate) <= 9 AND YEAR(a.enddate)=InYear   AND a.projectid=InProjId) 
       
           WHEN  InQuater=4 THEN ( SELECT COUNT(a.milestoneactivityid) FROM milestone_activity a WHERE MONTH(a.enddate) >= 10 AND MONTH(a.enddate) <= 12 AND YEAR(a.enddate)=InYear   AND a.projectid=InProjId) ELSE 0 END)     as newone  
        from dual;
 END */$$
