DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `pfms_timesheet_activity_analytics`(in InEmpId varchar(20), IN InProjectId VARCHAR(20), IN InFromDate VARCHAR(30), IN InToDate VARCHAR(30))
BEGIN
		SELECT b.TimeSheetActivityId, a.TimeSheetId, InEmpId AS 'EmpId', a.ActivityFromDate, b.ActivityId, InProjectId AS 'ProjectId', b.ActivityTypeId,
		(CASE c.Category WHEN 'T' THEN 'Technical' WHEN 'F' THEN 'Finance' WHEN 'M' THEN 'Managerial' WHEN 'L' THEN 'Logistic' WHEN 'O' THEN 'Others' END) AS 'ActivityName',
		(SELECT CONCAT(SEC_TO_TIME(SUM(TIME_TO_SEC(b.ActivityDuration))),'')) AS 'Duration'
		FROM pfms_timesheet a, pfms_timesheet_activity b, action_main c, action_assign d
		WHERE a.TimeSheetId = b.TimeSheetId AND a.IsActive = 1 AND b.IsActive = 1 AND b.ActivityId <> 0 AND b.ActivityId = d.ActionAssignId AND d.ActionMainId = c.ActionMainId AND d.IsActive = 1
		      and (CASE WHEN 'A'=InEmpId THEN 1=1 ELSE a.EmpId=InEmpId END) AND (CASE WHEN 'A'=InProjectId THEN 1=1 ELSE b.ProjectId=InProjectId END) AND a.ActivityFromDate BETWEEN InFromDate AND InToDate
		GROUP BY c.Category
		
		UNION 
		
		SELECT b.TimeSheetActivityId, a.TimeSheetId, InEmpId AS 'EmpId', a.ActivityFromDate, b.ActivityId, InProjectId AS 'ProjectId', b.ActivityTypeId, c.ActivityType,
                       (SELECT CONCAT(SEC_TO_TIME(SUM(TIME_TO_SEC(b.ActivityDuration))),'')) AS 'Duration'
                FROM pfms_timesheet a, pfms_timesheet_activity b, milestone_activity_type c
                WHERE a.TimeSheetId = b.TimeSheetId AND a.IsActive = 1 AND b.IsActive = 1 AND b.ActivityTypeId <> 0 AND b.ActivityTypeId = c.ActivityTypeId AND c.IsActive = 1
                      AND (CASE WHEN 'A'=InEmpId THEN 1=1 ELSE a.EmpId=InEmpId END) AND (CASE WHEN 'A'=InProjectId THEN 1=1 ELSE b.ProjectId=InProjectId END) AND a.ActivityFromDate BETWEEN InFromDate AND InToDate
                GROUP BY (CASE 
				WHEN 'A' = InEmpId THEN b.ActivityTypeId 
				ELSE CONCAT(a.EmpId, ',', b.ActivityTypeId) 
			END)

		;
                
	END */$$
DELIMITER ;

/* Procedure structure for procedure `pfms_timesheet_list` */

/*!50003 DROP PROCEDURE IF EXISTS  `pfms_timesheet_list` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `pfms_timesheet_list`(in InLabCode varchar(50), IN InLoginType VARCHAR(10), in InEmpId varchar(20), IN InFromDate varchar(30), IN InToDate VARCHAR(30))
BEGIN
		SELECT a.EmpId, CONCAT(IFNULL(CONCAT(a.title,' '),''), a.EmpName) AS 'EmpName', b.Designation, b.DesigCadre,
		concat(COALESCE(SEC_TO_TIME(SUM(TIME_TO_SEC(c.TotalDuration))), '00:00:00'),'') AS 'TotalDuration',
		COUNT(CASE WHEN TIME_TO_SEC(c.TotalDuration) < 28800 THEN 1 END) AS 'DeficitCount',
		CONCAT(SEC_TO_TIME(SUM(CASE WHEN TIME_TO_SEC(c.TotalDuration) < 28800 THEN 28800 - TIME_TO_SEC(c.TotalDuration) ELSE 0 END)),'') AS 'DeficitHours',
		COUNT(CASE WHEN TIME_TO_SEC(c.TotalDuration) > 28800 THEN 1 END) AS 'ExtraHoursCount',
		CONCAT(SEC_TO_TIME(SUM(CASE WHEN TIME_TO_SEC(c.TotalDuration) > 28800 THEN TIME_TO_SEC(c.TotalDuration) - 28800 ELSE 0 END)),'') AS 'ExtraHours'
		
		FROM employee a
		LEFT JOIN 
			employee_desig b ON a.DesigId = b.DesigId
		LEFT JOIN 
			pfms_timesheet c ON a.EmpId = c.EmpId AND c.ActivityFromDate BETWEEN InFromDate and InToDate
		WHERE a.IsActive = 1 AND a.LabCode = InLabCode
			AND CASE WHEN InLoginType in ('A','Z') THEN 1=1 ELSE ( InEmpId in (a.SuperiorOfficer, a.EmpId) ) END
			
		GROUP BY a.EmpId
		
		ORDER BY  FIELD(b.DesigCadre, 'DRDS','DRTC','Others') ASC, CASE WHEN a.SrNo = 0 THEN 1 ELSE 0 END, a.SrNo;
	END */$$
DELIMITER ;

