DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfts_sp_ModeWiseFiles`(inFromDate DATE, inToDate DATE)
BEGIN
 SELECT 'BF_FileReceived' AS OrderColumn, 
(SELECT COUNT(b.DemandNo) FROM pfts_filemilestone a, pfts_demandimms b WHERE DemandMode='op' AND b.DemandNo=a.DemandNo AND FileReceivedDate < inFromDate AND CASE WHEN SoDate IS NOT NULL THEN SoDate >= inFromDate AND FileReceivedDate < inFromDate ELSE FileReceivedDate < inFromDate END AND CASE WHEN a.IsActive=0 THEN a.DemandNo IN (SELECT ft.DemandNo FROM pfts_filetracking ft WHERE ft.DemandNo=a.DemandNo AND ft.EventDate > inFromDate AND ft.EventId=27) ELSE a.IsActive=1 END) AS OT,
(SELECT COUNT(b.DemandNo) FROM pfts_filemilestone a, pfts_demandimms b WHERE DemandMode='lm' AND b.DemandNo=a.DemandNo AND FileReceivedDate < inFromDate AND CASE WHEN SoDate IS NOT NULL THEN SoDate >= inFromDate AND FileReceivedDate < inFromDate ELSE FileReceivedDate < inFromDate END AND CASE WHEN a.IsActive=0 THEN a.DemandNo IN (SELECT ft.DemandNo FROM pfts_filetracking ft WHERE ft.DemandNo=a.DemandNo AND ft.EventDate > inFromDate AND ft.EventId=27) ELSE a.IsActive=1 END) AS LT,
(SELECT COUNT(b.DemandNo) FROM pfts_filemilestone a, pfts_demandimms b WHERE DemandMode='st' AND b.DemandNo=a.DemandNo AND FileReceivedDate < inFromDate AND CASE WHEN SoDate IS NOT NULL THEN SoDate >= inFromDate AND FileReceivedDate < inFromDate ELSE FileReceivedDate < inFromDate END AND CASE WHEN a.IsActive=0 THEN a.DemandNo IN (SELECT ft.DemandNo FROM pfts_filetracking ft WHERE ft.DemandNo=a.DemandNo AND ft.EventDate > inFromDate AND ft.EventId=27) ELSE a.IsActive=1 END) AS ST,
(SELECT COUNT(b.DemandNo) FROM pfts_filemilestone a, pfts_demandimms b WHERE DemandMode='pr' AND b.DemandNo=a.DemandNo AND FileReceivedDate < inFromDate AND CASE WHEN SoDate IS NOT NULL THEN SoDate >= inFromDate AND FileReceivedDate < inFromDate ELSE FileReceivedDate < inFromDate END AND CASE WHEN a.IsActive=0 THEN a.DemandNo IN (SELECT ft.DemandNo FROM pfts_filetracking ft WHERE ft.DemandNo=a.DemandNo AND ft.EventDate > inFromDate AND ft.EventId=27) ELSE a.IsActive=1 END) AS PAC,
(SELECT COUNT(b.DemandNo) FROM pfts_filemilestone a, pfts_demandimms b WHERE DemandMode='lp' AND b.DemandNo=a.DemandNo AND FileReceivedDate < inFromDate  AND CASE WHEN SoDate IS NOT NULL THEN SoDate >= inFromDate AND FileReceivedDate < inFromDate ELSE FileReceivedDate < inFromDate END AND CASE WHEN a.IsActive=0 THEN a.DemandNo IN (SELECT ft.DemandNo FROM pfts_filetracking ft WHERE ft.DemandNo=a.DemandNo AND ft.EventDate > inFromDate AND ft.EventId=27) ELSE a.IsActive=1 END) AS LPC,
