/* Procedure structure for procedure `Pfts_sp_ModeWiseMonitoringOrders` */

/*!50003 DROP PROCEDURE IF EXISTS  `Pfts_sp_ModeWiseMonitoringOrders` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Pfts_sp_ModeWiseMonitoringOrders`(inFromDate DATE, inToDate DATE)
BEGIN
SELECT
(CASE WHEN b.DemandMode='GM' THEN 'GeM'  WHEN b.DemandMode='lm' THEN 'LT'  WHEN b.DemandMode='st' THEN 'ST' WHEN b.DemandMode='lp' THEN 'LPC'
 WHEN b.DemandMode='pr' THEN 'PAC'  WHEN b.DemandMode='op' THEN 'OT'  WHEN b.DemandMode='gl' THEN 'GL'
 ELSE 'z-OTHERS' END)
AS DemandMode,
  a.DemandNo, b.ProjectCode, b.ItemFor, b.EstimatedCost, 
a.SoNo, a.FileReceivedDate, a.SoDate, DATEDIFF(a.SoDate, a.FileReceivedDate)AS 'NoOfDays'  
FROM Pfts_filemilestone a,  Pfts_demandimms b 
WHERE  b.DemandNo=a.DemandNo AND a.SoDate BETWEEN inFromDate AND inToDate 
AND CASE WHEN a.IsActive=0 THEN a.DemandNo 
IN (SELECT ft.DemandNo FROM Pfts_filetracking ft WHERE ft.DemandNo=a.DemandNo AND ft.EventDate > inFromDate AND ft.EventId=27) ELSE a.DemandNo=b.DemandNo END
ORDER BY DemandMode, NoOfDays;
 END */$$
DELIMITER ;

/* Procedure structure for procedure `pfts_sp_WeeklyReport` */

/*!50003 DROP PROCEDURE IF EXISTS  `pfts_sp_WeeklyReport` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `pfts_sp_WeeklyReport`(InAsOnDate DATE)
BEGIN
 
 SELECT c.FileStageId, a.DemandNo, d.FileReceivedDate, a.ItemFor, 
(CASE WHEN a.DemandMode='lg' THEN 'GeM'  WHEN a.DemandMode='lm' THEN 'Limited'  WHEN a.DemandMode='st' THEN 'Single' WHEN a.DemandMode='GM' THEN 'GeM'
 WHEN a.DemandMode='pr' THEN 'Proprietary'  WHEN a.DemandMode='op' THEN 'Open'  WHEN a.DemandMode='gl' THEN 'Global' WHEN a.DemandMode='lp' THEN 'LPC'
 WHEN a.DemandMode='ro' THEN 'Repeat Order'  ELSE a.DemandMode END)
 AS DemandMode, a.ProjectCode,  a.EstimatedCost, 
 d.TenderNo, d.TenderDate, d.TenderDueDate, d.SoNo, d.SoDate, d.DueDate, d.TotalCost, d.RinNo, d.ReceiptDate, d.InspectionDate, d.CrvNo, d.CrvDate, d.InBillDate, d.OutBillDate, 
 b.EventId, (c.EventName) AS 'Current Status', DATEDIFF(CURDATE() , d.FileReceivedDate) AS NoOfDays, b.Remarks, b.EventDate, 
 (CASE WHEN c.FileStageId='A' THEN 'EAccounting' WHEN c.FileStageId='B' THEN 'FBill' WHEN c.FileStageId='D' THEN 'ADemand' WHEN c.FileStageId='R' THEN 'DReceipt' WHEN c.FileStageId='S' THEN 'CSupplyOrder' 
 WHEN c.FileStageId='T' THEN 'BTender' ELSE c.FileStageId END) AS FileStage
FROM pfts_demandimms a, pfts_filetracking b, pfts_fileevents c, pfts_filemilestone d WHERE a.DemandNo=b.DemandNo AND b.EventId=c.FileEventId
AND a.DemandNo=d.DemandNo AND d.ChequeDate IS NULL
AND d.FileReceivedDate <= InAsOnDate AND c.FileStageId<>'Z'
ORDER BY FileStage,d.FileReceivedDate DESC ;
 
 END */$$
DELIMITER ;

