/* Procedure structure for procedure `Project_Health_Finance_Data` */

/*!50003 DROP PROCEDURE IF EXISTS  `Project_Health_Finance_Data` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Project_Health_Finance_Data`(InEmpId BIGINT(20),InLoginType VARCHAR(20),InLabCode VARCHAR(20))
BEGIN

 CALL Pfms_Emp_Projectids (InEmpId,InLoginType,InLabCode,@proid ); 
 
 /* dg = X | secretary = K  */

SELECT 1 AS 'budgetheadid','Capital' AS 'BudgetHeadDescription', 
IFNULL(ROUND(SUM(cd.AllotmentCost)/10000000,2),0.00) AS 'Allotment' , IFNULL(ROUND(SUM(cd.Expenditure)/10000000,2),0.00) AS 'Expenditure', IFNULL(ROUND(SUM(cd.Balance)/10000000,2),0.00) AS 'Balance'
FROM pfms_ccm_data cd 
WHERE cd.budgetheadid=1  AND
CASE WHEN 'Y'=(SELECT iscluster FROM lab_master WHERE labcode=InLabCode) 
	THEN 
		CASE WHEN '9'=(SELECT clusterid FROM lab_master WHERE labcode=InLabCode) THEN labcode IN (SELECT labcode FROM lab_master ) 
			ELSE labcode IN (SELECT labcode FROM lab_master WHERE clusterid=(SELECT clusterid FROM lab_master WHERE labcode=InLabCode) AND iscluster='N' ) END  
	ELSE cd.projectcode IN ( SELECT pm1.ProjectCode FROM project_master pm1 WHERE  FIND_IN_SET(pm1.projectid, @proId) >0 ) END 
UNION

SELECT 2 AS 'budgetheadid','Revenue' AS 'BudgetHeadDescription', 
IFNULL(ROUND(SUM(cd.AllotmentCost)/10000000,2),0.00) AS 'Allotment' , IFNULL(ROUND(SUM(cd.Expenditure)/10000000,2),0.00) AS 'Expenditure', IFNULL(ROUND(SUM(cd.Balance)/10000000,2),0.00) AS 'Balance'
FROM pfms_ccm_data cd 
WHERE cd.budgetheadid=2 AND
CASE WHEN 'Y'=(SELECT iscluster FROM lab_master WHERE labcode=InLabCode) 
	THEN 
		CASE WHEN '9'=(SELECT clusterid FROM lab_master WHERE labcode=InLabCode) THEN labcode IN (SELECT labcode FROM lab_master ) 
			ELSE labcode IN (SELECT labcode FROM lab_master WHERE clusterid=(SELECT clusterid FROM lab_master WHERE labcode=InLabCode) AND iscluster='N' ) END  
	ELSE cd.projectcode IN ( SELECT pm1.ProjectCode FROM project_master pm1 WHERE  FIND_IN_SET(pm1.projectid, @proId) >0 ) END 

UNION

SELECT 3 AS 'budgetheadid','Others' AS 'BudgetHeadDescription', 
