	IFNULL( (SUM(EBTotalToBeHeld)),0) AS 'EBTotalToBeHeld',
	
	IFNULL( (SUM(PJBHeld)),0) AS 'TotalPJBHeld',  
	IFNULL( (SUM(PJBPending)),0) AS 'TotalPJBPending',  
	IFNULL( (SUM(PJBTotal)),0) AS 'PJBOverAllTotal',    
	IFNULL( (SUM(PJBTotalToBeHeld)),0) AS 'PJBTotalToBeHeld',  
	IFNULL( CEIL((( SUM(PJBHeld) /  SUM(PJBTotal) )*100)),0) AS 'PJBHeld%',
	IFNULL( CEIL((( SUM(PJBPending) /  SUM(PJBTotal) )*100)),0) AS 'PJBPending%',
	
	IFNULL( (SUM(PMBHeld)),0) AS 'TotalPMBHeld',  
	IFNULL( (SUM(PMBPending)),0) AS 'TotalPMBPending',   
	IFNULL( (SUM(PMBTotal)),0) AS 'PMBOverAllTotal',  
	IFNULL( (SUM(PMBTotalToBeHeld)),0) AS 'PMBTotalToBeHeld',  
	IFNULL( CEIL((( SUM(PMBHeld) /  SUM(PMBTotal) )*100)),0) AS 'PMBHeld%',
	IFNULL( CEIL((( SUM(PMBPending) /  SUM(PMBTotal) )*100)),0) AS 'PMBPending%',
	
	IFNULL( (SUM(ABHeld)),0) AS 'TotalABHeld',  
	IFNULL( (SUM(ABPending)),0) AS 'TotalABPending',  
	IFNULL( (SUM(ABTotal)),0) AS 'ABOverAllTotal',   
	IFNULL( (SUM(ABTotalToBeHeld)),0) AS 'ABTotalToBeHeld',
	IFNULL( CEIL((( SUM(ABHeld) /  SUM(ABTotal) )*100)),0) AS 'ABHeld%',
	IFNULL( CEIL((( SUM(ABPending) /  SUM(ABTotal) )*100)),0) AS 'ABPending%'
	
	FROM project_health ph
	WHERE CASE WHEN InProjId='A' THEN ph.ProjectCode IN (
			SELECT a.ProjectCode
			FROM project_master a, employee e ,employee f
			WHERE  a.isactive=1 AND e.empid=InEmpId AND f.empid=a.projectdirector 
				AND CASE WHEN 'Y'=(SELECT iscluster FROM lab_master WHERE labcode=InLabCode) THEN 1=1 ELSE a.labcode = InLabCode END 
				AND CASE WHEN InLoginType IN ('A','Z','Y','L','E','X','K','I','C') OR 'Y'=(SELECT iscluster FROM lab_master WHERE labcode=InLabCode) THEN 1=1 
				ELSE a.ProjectCode IN (SELECT pm.ProjectCode FROM project_employee b,project_master pm WHERE pm.ProjectId=b.ProjectId AND b.isactive=1 AND b.empid=InEmpId 
				UNION 
				SELECT pm.ProjectCode FROM project_master pm WHERE InEmpId=a.projectdirector) END
			) 
		ELSE projectid=InProjId END 
		AND CASE WHEN 'Y'=(SELECT iscluster FROM lab_master WHERE labcode=InLabCode) AND InAll='Y' 
			THEN 
				CASE 
					WHEN '9'=(SELECT clusterid FROM lab_master WHERE labcode=InLabCode)
					THEN labcode IN (SELECT labcode FROM lab_master ) 
					ELSE labcode IN (SELECT labcode FROM lab_master WHERE clusterid=(SELECT clusterid FROM lab_master WHERE labcode=InLabCode) AND iscluster='N' ) END  
		ELSE labcode=InLabCode END 
		;

/* InAll - Y , does not compare labcode
   InAll - N , compare the labcode	
 Above Subquery is as good as Pfms_Emp_ProjectList */

END */$$
