DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Project_Rpt_Financial_Status`(IN InProjectId INTEGER, IN InRupees INTEGER)
BEGIN
SELECT  bi.HeadOfAccounts AS BudgetHeadDescription, IFNULL(round(SUM(b.SanctionCost/InRupees),2),0)AS TotalSanction,
(SELECT IFNULL(ROUND(SUM(ab.SanctionCost/InRupees),2),0) FROM project_hoa ab WHERE  ab.SanctionCost > 0  AND ab.ReFe='RE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId AND ab.BudgetItemId=b.BudgetItemId)AS ReSanction, 
(SELECT IFNULL(ROUND(SUM(ab.SanctionCost/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='FE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId  AND ab.BudgetItemId=b.BudgetItemId)AS FeSanction, 
(SELECT IFNULL(ROUND(SUM(ab.Expenditure/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='RE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId  AND ab.BudgetItemId=b.BudgetItemId)AS ReExpenditure,
(SELECT IFNULL(ROUND(SUM(ab.Expenditure/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='FE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId AND ab.BudgetItemId=b.BudgetItemId)AS FeExpenditure,
(SELECT IFNULL(ROUND(SUM(ab.OutCommitment/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND ab.ReFe='RE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId  AND ab.BudgetItemId=b.BudgetItemId)AS ReOutCommitment,
(SELECT IFNULL(ROUND(SUM(ab.OutCommitment/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND ab.ReFe='FE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId  AND ab.BudgetItemId=b.BudgetItemId)AS FeOutCommitment,
(SELECT IFNULL(ROUND(SUM(ab.Dipl/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='RE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId AND ab.BudgetItemId=b.BudgetItemId)AS ReDipl,
(SELECT IFNULL(ROUND(SUM(ab.Dipl/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='FE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId AND ab.BudgetItemId=b.BudgetItemId)AS FeDipl,
(SELECT IFNULL(ROUND(SUM(ab.Balance/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='RE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId AND ab.BudgetItemId=b.BudgetItemId)AS ReBalance,
(SELECT IFNULL(ROUND(SUM(ab.Balance/InRupees),2),0) FROM project_hoa ab WHERE ab.SanctionCost > 0  AND  ab.ReFe='FE' AND ProjectId=InProjectId AND ab.BudgetHeadId=b.BudgetHeadId AND ab.BudgetItemId=b.BudgetItemId)AS FeBalance,
b.BudgetHeadId, b.ProjectId,c.ProjectCode, bi.MajorHead, bi.MinorHead
 FROM budget_head a, project_hoa b, project_master c, budget_item bi 
 WHERE  a.BudgetHeadId=b.BudgetHeadId  AND b.ProjectId=c.ProjectId  AND b.ProjectId=InProjectId
 AND b.BudgetItemId=bi.BudgetItemId AND b.SanctionCost > 0 
 GROUP BY a.BudgetHeadId, bi.MajorHead, bi.MinorHead, bi.BudgetItemId 
 ORDER BY  bi.MajorHead, bi.MinorHead, b.BudgetHeadId, bi.BudgetItemId;
 
     END */$$
