/* Procedure structure for procedure `Stats_EmpListBasedOnLoginType` */

/*!50003 DROP PROCEDURE IF EXISTS  `Stats_EmpListBasedOnLoginType` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`192.168.1.17` PROCEDURE `Stats_EmpListBasedOnLoginType`(InEmpNo VARCHAR(20),InLabCode VARCHAR(20),InLoginType VARCHAR(5))
BEGIN
/***************USER***************************/
    IF  InLoginType = 'U' THEN
        SELECT e.EmpNo,e.EmpId,e.EmpName,e.DesigId,l.LoginType,d.Designation FROM employee e LEFT JOIN login l ON l.EmpId=e.EmpId 
        LEFT JOIN employee_desig d ON d.DesigId= e.DesigId
        WHERE e.isactive = 1 AND e.LabCode = InLabCode AND InEmpNo = e.EmpNo
        ORDER BY CASE WHEN e.Srno = 0 THEN 1 ELSE 0 END, e.Srno ASC;

/***************DH***************************/
    ELSEIF InLoginType = 'D' THEN
         SELECT DivisionId INTO @DivisionIdData FROM employee WHERE EmpNo = InEmpNo;
         SELECT e.EmpNo,e.EmpId,e.EmpName,e.DesigId,l.LoginType,d.Designation FROM employee e LEFT JOIN login l ON l.EmpId=e.EmpId 
         LEFT JOIN employee_desig d ON d.DesigId= e.DesigId  WHERE  e.isactive = 1 AND e.LabCode = InLabCode AND e.DivisionId =  @DivisionIdData 
         ORDER BY CASE WHEN e.Srno = 0 THEN 1 ELSE 0 END, e.Srno ASC;
            
 /***************GH***************************/         
    ELSEIF InLoginType = 'G' THEN
         SELECT d.GroupId INTO @GroupIdData FROM employee s , division_master d WHERE s.EmpNo = InEmpNo AND s.DivisionId=d.DivisionId;
         SELECT e.EmpNo,e.EmpId,e.EmpName,e.DesigId,l.LoginType,d.Designation FROM employee e 
         LEFT JOIN login l ON l.EmpId=e.EmpId  LEFT JOIN employee_desig d ON d.DesigId= e.DesigId 
         LEFT JOIN division_master s ON e.DivisionId=s.DivisionId
          WHERE  e.isactive = 1 AND e.LabCode = InLabCode  AND s.GroupId=@GroupIdData
         ORDER BY CASE WHEN e.Srno = 0 THEN 1 ELSE 0 END, e.Srno ASC;  
         
  /***************PD***************************/       
   ELSEIF InLoginType = 'P' THEN
   SELECT z.EmpId INTO @EmpIdData FROM employee z WHERE z.EmpNo=InEmpNo;
   SELECT DISTINCT e.EmpNo, e.EmpId FROM employee e 
   JOIN project_employee pe ON e.EmpId = pe.EmpId JOIN project_master pm ON pe.ProjectId = pm.ProjectId WHERE pm.ProjectDirector = @EmpIdData
   UNION
   SELECT e.EmpNo, e.EmpId FROM employee e WHERE e.EmpNo=InEmpNo;

   /***************FOR ALL OTHERS***************************/           
    ELSE
    /**Default case when InLoginType  'A', 'Z'**/
    SELECT e.EmpNo,e.EmpId,e.EmpName,e.DesigId,l.LoginType,d.Designation FROM employee e LEFT JOIN login l ON l.EmpId=e.EmpId
    LEFT JOIN employee_desig d ON d.DesigId= e.DesigId WHERE  e.isactive = 1 AND e.LabCode = InLabCode 
    ORDER BY CASE WHEN e.Srno = 0 THEN 1 ELSE 0 END, e.Srno ASC;
    /****Default CASE END***/
        

    END IF;
END */$$
DELIMITER ;

